import React from 'react';
import styles from '../../core/styles/components.module.css';

// Helper function to download JSON data
const downloadJSON = (data, filename) => {
  const json = JSON.stringify(data, null, 2);
  const blob = new Blob([json], { type: 'application/json' });
  const href = URL.createObjectURL(blob);

  const link = document.createElement('a');
  link.href = href;
  link.download = filename;
  document.body.appendChild(link);
  link.click();

  document.body.removeChild(link);
  URL.revokeObjectURL(href);
};

// In-house Marketing Assessment Results View
const InhouseResultsView = ({ results }) => {
  console.log('InhouseResultsView received results:', results);

  // Check if results is undefined or missing
  if (!results) {
    console.error('InhouseResultsView: results prop is undefined or null');
    return <div className={styles.errorMessage}>No results data available. Please try again.</div>;
  }

  // Log all the keys in the results object
  console.log('InhouseResultsView results keys:', Object.keys(results));

  const { executiveSummary, marketPosition, quickWins, strategicRoadmap, expectedOutcomes, nextSteps, maturityLevels } = results;

  // Check if required properties exist
  console.log('Executive Summary data:', executiveSummary);
  console.log('Market Position data:', marketPosition);
  console.log('Quick Wins data:', quickWins);
  console.log('Strategic Roadmap data:', strategicRoadmap);
  console.log('Expected Outcomes data:', expectedOutcomes);
  console.log('Next Steps data:', nextSteps);
  console.log('Maturity Levels data:', maturityLevels);

  // Early validation to catch issues
  if (!executiveSummary || !marketPosition || !quickWins || !strategicRoadmap || !expectedOutcomes || !nextSteps || !maturityLevels ||
      !marketPosition.overview || !marketPosition.strengths || !marketPosition.challenges) {
    console.error('InhouseResultsView: Missing required sections in results', {
      hasExecutiveSummary: !!executiveSummary,
      hasMarketPosition: !!marketPosition,
      hasQuickWins: !!quickWins,
      hasStrategicRoadmap: !!strategicRoadmap,
      hasExpectedOutcomes: !!expectedOutcomes,
      hasNextSteps: !!nextSteps,
      hasMaturityLevels: !!maturityLevels
    });
    return (
      <div className={styles.errorMessage}>
        <h2>Missing Assessment Data</h2>
        <p>Some sections of your assessment results are missing. Available sections:</p>
        <ul>
          {executiveSummary && <li>Executive Summary</li>}
          {marketPosition && <li>Market Position</li>}
          {quickWins && <li>Quick Wins</li>}
          {strategicRoadmap && <li>Strategic Roadmap</li>}
          {expectedOutcomes && <li>Expected Outcomes</li>}
          {nextSteps && <li>Next Steps</li>}
          {maturityLevels && <li>Maturity Assessment</li>}
        </ul>
      </div>
    );
  }

  // Calculate overall effectiveness based on headline
  const getOverallScore = () => {
    if (!executiveSummary || !executiveSummary.headline) return 50;
    const headline = executiveSummary.headline.toLowerCase();
    if (headline.includes('urgent action') || headline.includes('at risk')) return 35;
    if (headline.includes('early stage') || headline.includes('adapting')) return 55;
    if (headline.includes('building capability') || headline.includes('keeping pace')) return 75;
    if (headline.includes('transformation ready') || headline.includes('leading')) return 90;
    return 65; // Default middle score
  };

  const overallScore = getOverallScore();

  // Get color for maturity progress bars
  const getMaturityColor = (score) => {
    if (score < 30) return '#dc3545'; // Red - poor
    if (score < 50) return '#fd7e14'; // Orange - needs work
    if (score < 70) return '#ffc107'; // Yellow - average
    if (score < 85) return '#20c997'; // Teal - good
    return '#28a745'; // Green - excellent
  };

  // Helper function to get CSS class based on impact level
  const getImpactClass = (impact) => {
    if (!impact) return styles.impactDefault;
    const impactLower = impact.toLowerCase();
    if (impactLower === 'high') return styles.impactHigh;
    if (impactLower === 'medium') return styles.impactMedium;
    return styles.impactLow;
  };

  // Helper function to get CSS class based on effort level
  const getEffortClass = (effort) => {
    if (!effort) return styles.effortDefault;
    const effortLower = effort.toLowerCase();
    if (effortLower === 'low') return styles.effortLow;
    if (effortLower === 'medium') return styles.effortMedium;
    return styles.effortHigh;
  };

  return (
    <div className={styles.inhouseResultsContainer}>
      {/* Executive Summary Section */}
      <div className={styles.executiveSummarySection}>
        <h2 className={styles.sectionTitle}>Executive Summary</h2>
        <div className={styles.executiveSummaryContent}>
          <h3 className={styles.headline}>{executiveSummary.headline}</h3>
          <p className={styles.executiveSummaryText}>{executiveSummary.summary}</p>
        </div>

        {/* Maturity Level Overview */}
        <div className={styles.maturityLevelOverview}>
          <h4 className={styles.maturitySubtitle}>Overall Effectiveness</h4>
          <div className={styles.overallScoreContainer}>
            <div className={styles.scoreProgressBar}>
              <div 
                className={styles.scoreProgressFill}
                style={{ width: `${overallScore}%`, backgroundColor: getMaturityColor(overallScore) }}
              />
            </div>
            <div className={styles.scoreLabel}>{overallScore}%</div>
          </div>
        </div>
      </div>

      {/* Market Position Section */}
      <div className={styles.marketPositionSection}>
        <h2 className={styles.sectionTitle}>Current Market Position</h2>
        <div className={styles.marketPositionContent}>
          <p className={styles.marketPositionText}>{marketPosition.overview}</p>

          <div className={styles.marketInsights}>
            <div className={styles.insightColumn}>
              <h4 className={styles.insightTitle}>Strengths</h4>
              <ul className={styles.insightsList}>
                {marketPosition.strengths && Array.isArray(marketPosition.strengths) ? 
                  marketPosition.strengths.map((strength, idx) => (
                    <li key={idx} className={styles.insightItem}>{strength}</li>
                  )) : 
                  <li className={styles.insightItem}>No strengths data available</li>
                }
              </ul>
            </div>

            <div className={styles.insightColumn}>
              <h4 className={styles.insightTitle}>Challenges</h4>
              <ul className={styles.insightsList}>
                {marketPosition.challenges && Array.isArray(marketPosition.challenges) ? 
                  marketPosition.challenges.map((challenge, idx) => (
                    <li key={idx} className={styles.insightItem}>{challenge}</li>
                  )) : 
                  <li className={styles.insightItem}>No challenges data available</li>
                }
              </ul>
            </div>
          </div>
        </div>
      </div>

      {/* Quick Wins Section */}
      <div className={styles.quickWinsSection}>
        <h2 className={styles.sectionTitle}>Quick Wins</h2>
        <p className={styles.sectionDescription}>{quickWins.description}</p>

        <div className={styles.quickWinsList}>
          {quickWins.items && Array.isArray(quickWins.items) ? quickWins.items.map((item, idx) => (
            <div key={idx} className={styles.quickWinItem}>
              <h4 className={styles.quickWinTitle}>{item.title}</h4>
              <p className={styles.quickWinDescription}>{item.description}</p>

              <div className={styles.badgeContainer}>
                <span className={`${styles.badge} ${getImpactClass(item.impact)}`}>
                  Impact: {item.impact}
                </span>
                <span className={`${styles.badge} ${getEffortClass(item.effort)}`}>
                  Effort: {item.effort}
                </span>
              </div>
            </div>
          )) : <div className={styles.quickWinItem}>No quick wins available</div>}
        </div>
      </div>

      {/* Strategic Roadmap Section */}
      <div className={styles.strategicRoadmapSection}>
        <h2 className={styles.sectionTitle}>Strategic Roadmap</h2>
        <p className={styles.sectionDescription}>{strategicRoadmap.description}</p>

        <div className={styles.roadmapPhases}>
          {strategicRoadmap.phases && Array.isArray(strategicRoadmap.phases) ? strategicRoadmap.phases.map((phase, idx) => (
            <div key={idx} className={styles.roadmapPhase}>
              <div className={styles.phaseHeader}>
                <h3 className={styles.phaseTitle}>{phase.title}</h3>
                <span className={styles.phaseTimeline}>{phase.timeline}</span>
              </div>
              <p className={styles.phaseDescription}>{phase.description}</p>

              <div className={styles.phaseMilestones}>
                <h4 className={styles.milestonesTitle}>Key Milestones:</h4>
                <ul className={styles.milestonesList}>
                  {phase.milestones && Array.isArray(phase.milestones) ? phase.milestones.map((milestone, mIdx) => (
                    <li key={mIdx} className={styles.milestoneItem}>{milestone}</li>
                  )) : <li className={styles.milestoneItem}>No milestones available</li>}
                </ul>
              </div>
            </div>
          )) : <div className={styles.roadmapPhase}>No roadmap phases available</div>}
        </div>
      </div>

      {/* Expected Outcomes Section */}
      <div className={styles.expectedOutcomesSection}>
        <h2 className={styles.sectionTitle}>Expected Outcomes</h2>
        <p className={styles.sectionDescription}>{expectedOutcomes.description}</p>

        <div className={styles.outcomesList}>
          {expectedOutcomes.items && Array.isArray(expectedOutcomes.items) ? expectedOutcomes.items.map((item, idx) => (
            <div key={idx} className={styles.outcomeItem}>
              <h4 className={styles.outcomeTitle}>{item.title}</h4>
              <p className={styles.outcomeDescription}>{item.description}</p>

              {item.metrics && (
                <div className={styles.outcomeMetrics}>
                  <h5 className={styles.metricsTitle}>Key Metrics:</h5>
                  <ul className={styles.metricsList}>
                    {item.metrics && Array.isArray(item.metrics) ? item.metrics.map((metric, mIdx) => (
                      <li key={mIdx} className={styles.metricItem}>{metric}</li>
                    )) : <li className={styles.metricItem}>No metrics available</li>}
                  </ul>
                </div>
              )}
            </div>
          )) : <div className={styles.outcomeItem}>No expected outcomes available</div>}
        </div>
      </div>

      {/* Next Steps Section */}
      <div className={styles.nextStepsSection}>
        <h2 className={styles.sectionTitle}>Next Steps</h2>
        <p className={styles.sectionDescription}>{nextSteps.description}</p>

        <div className={styles.ctaContainer}>
          <div className={styles.ctaPrimary}>
            <h3 className={styles.ctaTitle}>{nextSteps.primaryCTA.title}</h3>
            <p className={styles.ctaDescription}>{nextSteps.primaryCTA.description}</p>
            <a href={nextSteps.primaryCTA.link} className={styles.ctaButton}>
              {nextSteps.primaryCTA.buttonText}
            </a>
          </div>

          <div className={styles.alternativeOptions}>
            <h4 className={styles.alternativesTitle}>Other Options:</h4>
            <div className={styles.alternativesList}>
              {nextSteps.alternatives && Array.isArray(nextSteps.alternatives) ? nextSteps.alternatives.map((alt, idx) => (
                <a key={idx} href={alt.link} className={styles.alternativeLink}>
                  {alt.text}
                </a>
              )) : <span className={styles.alternativeLink}>No alternative options available</span>}
            </div>
          </div>
        </div>
      </div>

      {/* Maturity Chart Section */}
      <div className={styles.maturityChartSection}>
        <h2 className={styles.sectionTitle}>Maturity Assessment</h2>
        <p className={styles.sectionDescription}>Your current digital marketing maturity levels across key capabilities:</p>

        <div className={styles.maturityChartContainer}>
          {maturityLevels && Array.isArray(maturityLevels) ? maturityLevels.map((level, idx) => (
            <div key={idx} className={styles.maturityItem}>
              <div className={styles.maturityLabelContainer}>
                <span className={styles.maturityLabel}>{level.area}</span>
                <span className={styles.maturityScore}>{level.score}%</span>
              </div>
              <div className={styles.maturityBarContainer}>
                <div 
                  className={styles.maturityBarFill}
                  style={{ width: `${level.score}%`, backgroundColor: getMaturityColor(level.score) }}
                />
              </div>
              <p className={styles.maturityDescription}>{level.description}</p>
            </div>
          )) : <div className={styles.maturityItem}>No maturity data available</div>}
        </div>
      </div>

      {/* JSON Download Section */}
      <div className={styles.downloadSection}>
        <button 
          className={styles.downloadButton}
          onClick={() => downloadJSON(results, 'inhouse-marketing-assessment-results.json')}
        >
          Download Assessment Data (JSON)
        </button>
        <p className={styles.downloadNote}>Download your complete assessment data for record keeping or further analysis.</p>
      </div>
    </div>
  );
};

export default InhouseResultsView;
