    calculate(responses, context) {
        console.log('Starting score calculation with responses:', Object.keys(responses).length);
        console.log('Context for calculation:', context);

        try {
            // Base scoring logic used by all assessments
            let scores = this.calculateDimensionScores(responses);

            // Apply custom scoring if provided
            if (this.customScoring?.calculate) {
                try {
                    const customScores = this.customScoring.calculate(scores, responses, context);
                    if (customScores) scores = customScores;
                } catch (error) {
                    console.error('Error in custom scoring calculation:', error);
                }
            }

            // Apply context-based adjustments
            scores = this.applyContextAdjustments(scores, context);

            // Calculate overall score
            const overall = this.calculateOverallScore(scores);

            // Generate insights
            const result = {
                scores: {
                    overall: overall,
                    categories: scores
                },
                insights: this.generateInsights({ overall, dimensions: scores }, context),
                report: this.generateReport({ overall, dimensions: scores }, context)
            };

            console.log('Final calculation result:', result);
            return result;
        } catch (error) {
            console.error('Error in score calculation:', error);

            // Return a fallback result to prevent UI errors
            return {
                scores: {
                    overall: 50,
                    categories: {
                        technology: { score: 50 },
                        process: { score: 50 },
                        people: { score: 50 },
                        strategy: { score: 50 }
                    }
