import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import QuestionCard from './QuestionCard';
import Navigation from './Navigation';
import styles from '../styles/components.module.css';

const DEBUG_MODE = process.env.NODE_ENV === 'development';

const DynamicQuestions = ({
    assessmentType,
    saveResponse,
    getResponse,
    getContext,
    onComplete,
    onBack,
    progress = 50
}) => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [allAnswers, setAllAnswers] = useState({});
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    // Helper function for debugging - only logs in development mode
    const debugLog = (...args) => {
        if (DEBUG_MODE) {
            console.log(...args);
        }
    };

    // Load questions from appropriate JSON files based on assessment type
    const loadQuestions = async () => {
        try {
            setLoading(true);
            console.log(`ðŸ” [${assessmentType}] LOADING QUESTIONS - DEBUGGING ISSUE`);

            // Load core questions
            let coreQuestions = [];
            let categoryQuestionCounts = {};

            // Function to recursively extract questions from nested structure
            const extractQuestionsFromNestedObject = (obj, path = '') => {
                let extractedQuestions = [];

                // Base case: If it's an array, we assume it's an array of questions
                if (Array.isArray(obj)) {
                    console.log(`ðŸ”¢ Found ${obj.length} questions array at path: ${path}`);
                    // Count questions per category for debugging
                    if (path) {
                        categoryQuestionCounts[path] = obj.length;
                    }
                    return obj;
                }

                // If it's an object, traverse its properties
                if (obj && typeof obj === 'object') {
                    for (const key in obj) {
                        const newPath = path ? `${path}.${key}` : key;

                        if (Array.isArray(obj[key])) {
                            // Found an array, add its items to our questions
                            console.log(`ðŸ”¢ Found ${obj[key].length} questions in ${newPath}`);
                            categoryQuestionCounts[newPath] = obj[key].length;
                            extractedQuestions = extractedQuestions.concat(obj[key]);
                        } else if (obj[key] && typeof obj[key] === 'object') {
                            // Found a nested object, recursively extract questions from it
                            const nestedQuestions = extractQuestionsFromNestedObject(obj[key], newPath);
                            if (nestedQuestions.length > 0) {
                                console.log(`âž• Adding ${nestedQuestions.length} questions from ${newPath}`);
                                extractedQuestions = extractedQuestions.concat(nestedQuestions);
                            }
                        }
                    }
                }

                return extractedQuestions;
            };

            // LOAD CORE QUESTIONS
            try {
                const coreModule = await import(`../../assessments/${assessmentType}/questions.json`);
                const coreData = coreModule.default || coreModule;

                console.log(`ðŸ“ [${assessmentType}] Core questions data structure:`, Object.keys(coreData));

                // Handle different possible structures
                if (Array.isArray(coreData)) {
                    // Direct array structure
                    coreQuestions = coreData;
                    console.log(`ðŸ“Š Loaded ${coreQuestions.length} core questions (direct array)`);
                } else if (coreData.coreQuestions) {
                    // Structure with coreQuestions property
                    if (Array.isArray(coreData.coreQuestions)) {
                        // coreQuestions is a direct array
                        coreQuestions = coreData.coreQuestions;
                        console.log(`ðŸ“Š Loaded ${coreQuestions.length} core questions from coreQuestions array`);
                    } else {
                        // coreQuestions is an object with nested question arrays
                        console.log(`ðŸ” Extracting questions from nested coreQuestions object with categories:`, Object.keys(coreData.coreQuestions));

                        // First, let's log all the categories and their question counts
                        for (const category in coreData.coreQuestions) {
                            if (Array.isArray(coreData.coreQuestions[category])) {
                                console.log(`ðŸ“Š Category ${category} has ${coreData.coreQuestions[category].length} direct questions`);
                            } else if (typeof coreData.coreQuestions[category] === 'object') {
                                console.log(`ðŸ” Category ${category} has nested structure:`, Object.keys(coreData.coreQuestions[category]));
                            }
                        }

                        coreQuestions = extractQuestionsFromNestedObject(coreData.coreQuestions, 'coreQuestions');
                        console.log(`ðŸ“Š Category question counts:`, categoryQuestionCounts);
                        console.log(`ðŸ“Š Total extracted core questions: ${coreQuestions.length}`);

                        // Verify by listing all question IDs
                        const coreQuestionIds = coreQuestions.map(q => q.id);
                        console.log(`ðŸ†” Core question IDs:`, coreQuestionIds);
                    }
                } else {
                    // Try to extract questions from the entire object
                    coreQuestions = extractQuestionsFromNestedObject(coreData);
                    console.log(`ðŸ“Š Extracted ${coreQuestions.length} questions from entire data object`);
                }

                if (coreQuestions.length === 0) {
                    console.error(`âŒ Could not find questions array in core data structure:`, Object.keys(coreData));
                }
            } catch (error) {
                console.error(`âŒ Error loading core questions for ${assessmentType}:`, error);
            }

            console.log(`ðŸ“Š Loaded ${coreQuestions.length} core questions`);

            // LOAD SERVICE QUESTIONS
            let serviceQuestions = [];
            const selectedServices = getContext('selectedServices');
            console.log('ðŸ¢ Selected services', selectedServices);

            try {
                // First try to load from service-questions.json
                let serviceModule;
                try {
                    serviceModule = await import(`../../assessments/${assessmentType}/service-questions.json`);
                    console.log(`âœ… Loaded service-questions.json for ${assessmentType}`);
                } catch (error) {
                    console.log(`âŒ No service-questions.json found for ${assessmentType}, trying activity-questions.json`);
                    // Fallback to activity-questions.json if service-questions.json doesn't exist
                    serviceModule = await import(`../../assessments/${assessmentType}/activity-questions.json`);
                    console.log(`âœ… Loaded activity-questions.json as fallback`);
                }

                const serviceData = serviceModule.default || serviceModule;
                const serviceQuestionsData = serviceData.serviceQuestions || {};
                console.log('ðŸ“ Service questions data structure', Object.keys(serviceData));

                // Handle service ID mappings (for matching with different naming conventions)
                const serviceIdMappings = {
                    'social': ['social', 'social-media', 'socialMedia'],
                    'paid-search': ['paid-search', 'paidSearch', 'sem', 'ppc'],
                    'content': ['content', 'content-marketing', 'contentMarketing'],
                    'seo': ['seo', 'search-engine-optimization'],
                    'email': ['email', 'email-marketing', 'emailMarketing'],
                    'web': ['web', 'website', 'web-development'],
                    'data': ['data', 'analytics', 'data-analytics'],
                    'crm': ['crm', 'customer-relationship-management'],
                    'automation': ['automation', 'marketing-automation'],
                    'strategy': ['strategy', 'marketing-strategy']
                };

                const ALLOCATION_THRESHOLD = 5; // 5% minimum allocation to include service questions

                // Check if selectedServices is available and correctly formatted
                if (selectedServices && typeof selectedServices === 'object') {
                    // Check threshold for including service-specific questions (minimum 5% allocation)
                    const allocationThreshold = 5; // 5%

                    // Map selected service IDs to their corresponding question categories
                    const selectedServiceIds = selectedServices
                        .filter(svc => svc.allocation >= allocationThreshold)
                        .map(svc => svc.id);

                    console.log('ðŸ”‘ Selected service IDs above threshold', selectedServiceIds);

                    // Extract service questions based on the structure
                    if (serviceData.serviceQuestions) {
                        console.log('ðŸ” Available service categories:', Object.keys(serviceData.serviceQuestions));

                        for (const serviceKey in serviceData.serviceQuestions) {
                            // Check if this service matches any of our selected services
                            // (either by direct ID match or by name fuzzy match)
                            const isServiceSelected = selectedServiceIds.some(id => {
                                // Direct match by ID
                                if (id === serviceKey) {
                                    console.log(`âœ… Found direct match for service: ${serviceKey}`);
                                    return true;
                                }

                                // Try to match by name (case insensitive partial match)
                                const serviceName = serviceData.serviceQuestions[serviceKey].serviceName;
                                if (serviceName) {
                                    // Check if the service ID contains part of the service name or vice versa
                                    const idMatch = id.toLowerCase().includes(serviceName.toLowerCase());
                                    const nameMatch = serviceName.toLowerCase().includes(id.toLowerCase());
                                    if (idMatch || nameMatch) {
                                        console.log(`âœ… Found fuzzy match: ${id} â‰ˆ ${serviceName} (${serviceKey})`);
                                        return true;
                                    }
                                }
                                return false;
                            });

                            if (isServiceSelected) {
                                const serviceInfo = serviceData.serviceQuestions[serviceKey];
                                if (serviceInfo.questions && Array.isArray(serviceInfo.questions)) {
                                    console.log(`ðŸ“Š Adding ${serviceInfo.questions.length} questions for service ${serviceKey}`);

                                    // Log the question IDs for debugging
                                    const questionIds = serviceInfo.questions.map(q => q.id);
                                    console.log(`ðŸ†” Service ${serviceKey} question IDs:`, questionIds);

                                    serviceQuestions = serviceQuestions.concat(serviceInfo.questions);
                                }
                            } else {
                                console.log(`âŒ Service ${serviceKey} not selected or below threshold`);
                            }
                        }
                    } else {
                        console.warn('âŒ No serviceQuestions property found in service data');
                    }
                } else {
                    console.warn('âŒ No services selected or invalid selectedServices format:', selectedServices);
                }
            } catch (error) {
                console.error('âŒ Error loading service questions:', error);
            }

            console.log(`ðŸ“Š Found ${serviceQuestions.length} service-specific questions`);

            // REMOVE DUPLICATES AND VALIDATE QUESTIONS
            const uniqueQuestions = [];
            const questionIds = new Set();

            // Process core questions first
            coreQuestions.forEach(q => {
                if (q && q.id && !questionIds.has(q.id)) {
                    // Ensure question has all required properties
                    if (!q.options || !Array.isArray(q.options) || q.options.length === 0) {
                        console.log(`âš ï¸ Skipping invalid question without options:`, q.id);
                        return;
                    }

                    questionIds.add(q.id);
                    uniqueQuestions.push(q);
                }
            });

            // Then add service questions, avoiding duplicates
            serviceQuestions.forEach(q => {
                if (q && q.id && !questionIds.has(q.id)) {
                    // Ensure question has all required properties
                    if (!q.options || !Array.isArray(q.options) || q.options.length === 0) {
                        console.log(`âš ï¸ Skipping invalid service question without options:`, q.id);
                        return;
                    }

                    questionIds.add(q.id);
                    uniqueQuestions.push(q);
                }
            });

            console.log(`ðŸ“Š Total unique questions to display: ${uniqueQuestions.length}`);
            console.log('ðŸ†” Question IDs:', Array.from(questionIds));

            setQuestions(uniqueQuestions);
        } catch (error) {
            console.error('âŒ Failed to load questions:', error);
        } finally {
            setLoading(false);
        }
    };

    // Load initial questions and answers
    useEffect(() => {
        const loadInitialData = async () => {
            // Get previously saved answers if available
            const savedAnswers = getResponse('answers') || {};
            debugLog('Loaded saved answers', savedAnswers);
            setAllAnswers(savedAnswers);

            // Load questions from JSON file
            await loadQuestions();
        };

        loadInitialData();
    }, [assessmentType, getResponse]);

    // Handle answer changes
    const handleAnswerChange = (questionId, answer) => {
        const updatedAnswers = {
            ...allAnswers,
            [questionId]: answer
        };

        setAllAnswers(updatedAnswers);
        saveResponse('answers', updatedAnswers);
        debugLog(`Answer updated for question ${questionId}:`, answer);
    };

    // Navigation functions
    const handleNext = () => {
        if (currentQuestion < questions.length - 1) {
            setCurrentQuestion(currentQuestion + 1);
            window.scrollTo(0, 0);
        } else {
            // Save final answers and complete
            saveResponse('answers', allAnswers);

            debugLog('Total questions:', questions.length);

            // Before completing, ensure selectedServices is stored in both context and responses
            if (typeof getContext === 'function') {
                const selectedServices = getContext('selectedServices');
                if (selectedServices && typeof saveResponse === 'function') {
                    // Save as a response to ensure it's available in both places
                    saveResponse('selectedServices', selectedServices);
                    debugLog('Saved selectedServices to responses as backup', selectedServices);
                }
            }

            // Set a global flag to indicate this component has completed with answers
            if (typeof window !== 'undefined') {
                window.dynamicQuestionsCompleted = true;
                window.dynamicQuestionsAnswerCount = Object.keys(allAnswers).length;
            }

            // Move to the next stage using onComplete callback
            if (onComplete) {
                onComplete();
            }
        }
    };

    const handleBack = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion(currentQuestion - 1);
            window.scrollTo(0, 0);
        } else {
            // Go back to previous component
            if (onBack) {
                onBack();
            }
        }
    };

    if (loading) {
        return (
            <div className={styles.loadingContainer}>
                <div className={styles.loadingSpinner}></div>
                <h3 className={styles.loadingText}>
                    Loading questions...
                </h3>
            </div>
        );
    }

    if (questions.length === 0) {
        return (
            <div className={styles.errorContainer}>
                <h3 className={styles.errorHeading}>
                    Error: No questions found for this assessment
                </h3>
                <button 
                    onClick={onBack} 
                    className={styles.errorButton}
                >
                    Go Back
                </button>
            </div>
        );
    }

    const currentQuestionData = questions[currentQuestion];

    return (
        <div className={styles.questionsContainer}>
            <div className={styles.questionHeader}>
                <h2 className={styles.assessmentTitle}>
                    {assessmentType === 'agency-vulnerability' 
                        ? 'Agency AI Vulnerability Assessment' 
                        : 'Marketing Assessment'}
                </h2>
                <p className={styles.questionCounter}>
                    Question {currentQuestion + 1} of {questions.length}
                </p>
            </div>

            {currentQuestionData && (
                <QuestionCard
                    question={currentQuestionData}
                    answer={allAnswers[currentQuestionData.id] || ''}
                    onAnswerChange={(answer) => handleAnswerChange(currentQuestionData.id, answer)}
                />
            )}

            <Navigation 
                currentStep={currentQuestion + 1}
                totalSteps={questions.length}
                onNext={handleNext}
                onBack={handleBack}
                nextDisabled={!allAnswers[currentQuestionData?.id]}
                progress={progress}
            />
        </div>
    );
};

export default DynamicQuestions;
