      console.log('Adding default strengths data');
      executiveSummary.strengths = ['Team shows willingness to adopt new technologies', 'Basic technical infrastructure is in place', 'Key marketing activities are already identified'];
    }
    if (!executiveSummary.weaknesses || !executiveSummary.weaknesses.length) {
      console.log('Adding default weaknesses data');
      executiveSummary.weaknesses = ['Limited automation expertise in current team', 'Manual processes consume significant time', 'Cross-channel coordination needs improvement'];
    }
    if (!marketPosition.competitiveAnalysis || !marketPosition.competitiveAnalysis.ahead || !marketPosition.competitiveAnalysis.ahead.length) {
      console.log('Adding default competitive advantages');
      marketPosition.competitiveAnalysis.ahead = ['Early adoption of some automation tools', 'Solid understanding of target audience', 'Willingness to innovate'];
    }
    if (!marketPosition.competitiveAnalysis || !marketPosition.competitiveAnalysis.behind || !marketPosition.competitiveAnalysis.behind.length) {
      console.log('Adding default competitive disadvantages');
      marketPosition.competitiveAnalysis.behind = ['Limited cross-channel integration', 'Manual reporting processes', 'Siloed marketing data'];
    }
    if (!quickWins || !quickWins.length) {
      console.log('Adding default quick wins');
      quickWins = [{
        title: 'Implement Content Calendar',
        description: 'Organize content production with a structured calendar',
        impact: 'High',
        effort: 'Low',
        implementation: ['Audit existing content', 'Define content themes', 'Create 90-day calendar']
      }, {
        title: 'Set Up Welcome Sequence',
        description: 'Create an automated email sequence for new subscribers',
        impact: 'High',
        effort: 'Medium',
        implementation: ['Map customer journey', 'Draft email sequence', 'Implement triggers']
