    }

    // Log summary of data prepared for view
    console.log('Data preparation complete. Summary:');
    console.log(`- Executive Summary: ${executiveSummary.strengths.length} strengths, ${executiveSummary.weaknesses.length} weaknesses`);
    console.log(`- Market Position: Position = ${marketPosition.position}`);
    console.log(`- Quick Wins: ${quickWins.length} items`);
    console.log(`- Strategic Roadmap: ${Object.keys(strategicRoadmap).length} sections`);
    console.log(`- Expected Outcomes: ${expectedOutcomes.length} items`);
    console.log(`- Maturity Levels: ${Object.keys(maturityLevels).length} dimensions`);

    // Complete results object including both original raw data and the formatted view data
    return {
      // For the InhouseResultsView component
      executiveSummary,
      marketPosition,
      quickWins,
      strategicRoadmap,
      expectedOutcomes,
      nextSteps,
      maturityLevels,
      // Original assessment metadata
      assessmentType: 'inhouse-marketing',
      timestamp: rawResults.timestamp || new Date().toISOString(),
      // Individual question responses
      responses: allResponses,
      // Context data
      context: {
        companySize,
        sector,
        selectedActivities
      },
      // Original results data
      results: {
        overall: overallScore,
        dimensions: dimensionScores,
        categoryScores: categoryScores,
        activityScores: activityScores,
        insights: insights,
        priorities: priorities,
        readinessLevel: readinessLevel,
        marketPosition: rawMarketPosition,
        savingsPotential: savingsPotential
      },
      // Engine data for completeness
      engineData: {
        allResponses: allResponses,
        context: {
          companySize,
          sector,
          selectedActivities
        },
        progressData: {
          current: 5,
          total: 6,
          percentage: 80,
          stageName: 'results'
        },
        completionStatus: true
      },
      // Include raw data for debugging
      _rawData: rawResults
    };
  }
}
export default InhouseMarketingResultsAdapter;

