/**
 * ResultsAdapter for inhouse-marketing assessment
 * Transforms raw scoring engine output into a consistent format for the results view
 */

class InhouseMarketingResultsAdapter {
  /**
   * Adapts raw results from the scoring engine into the format needed by the view component
   * @param {Object} rawResults - Raw results object from the scoring engine
   * @param {Function} getResponse - Function to retrieve saved responses
   * @returns {Object} - Formatted results object
   */
  adaptResults(rawResults, getResponse) {
    console.log('InhouseMarketingResultsAdapter: Starting adaptation process');

    // Critical validation - ensure we have data to work with
    if (!rawResults) {
      console.error('InhouseMarketingResultsAdapter: No raw results provided');
      return null;
    }

    console.log('InhouseMarketingResultsAdapter: Raw results overview:', {
      hasOverall: Boolean(rawResults.overall),
      hasDimensions: Boolean(rawResults.dimensions),
      hasInsights: Boolean(rawResults.insights),
      hasCategories: Boolean(rawResults.categoryScores)
    });

    // Step 1: Extract core assessment data with validation
    const overallScore = typeof rawResults.overall === 'number' ? rawResults.overall : 35;

    // Extract dimension scores with validation
    const dimensionScores = rawResults.dimensions || {};
    const validatedDimensions = {
      humanReadiness: typeof dimensionScores.humanReadiness === 'number' ? dimensionScores.humanReadiness : 35,
      technicalReadiness: typeof dimensionScores.technicalReadiness === 'number' ? dimensionScores.technicalReadiness : 30,
      processMaturity: typeof dimensionScores.processMaturity === 'number' ? dimensionScores.processMaturity : 40,
      activityAutomation: typeof dimensionScores.activityAutomation === 'number' ? dimensionScores.activityAutomation : 40
    };

    // Extract category scores with validation
    const categoryScores = rawResults.categoryScores || {};
    const validatedCategories = {};
    Object.keys(categoryScores).forEach(category => {
      if (typeof categoryScores[category] === 'number') {
        validatedCategories[category] = categoryScores[category];
      }
    });

    // Activity scores for detailed recommendations
    const activityScores = rawResults.activityScores || {};

    // Step 2: Get all user responses to generate accurate insights
    const allResponses = {};
    try {
      // Get all saved question responses from various sections
      const questionTypes = ['qualifying', 'service_selection', 'dynamic_questions'];
      questionTypes.forEach(type => {
        const savedData = getResponse(type);
        if (savedData) {
          Object.assign(allResponses, savedData);
        }
      });
      console.log('InhouseMarketingResultsAdapter: Retrieved responses for sections', questionTypes);
    } catch (error) {
      console.error('InhouseMarketingResultsAdapter: Error retrieving responses:', error);
    }

    // Step 3: Extract contextual information about the assessment
    const companySize = allResponses.company_size || 'small';
    const sector = allResponses.sector || {};
    const sectorName = sector.name || 'General Business';

    // Parse selected services/activities
    const selectedServices = allResponses.selectedServices || {};
    let selectedActivities = Object.keys(selectedServices).filter(key => selectedServices[key] > 0.05);
    if (!selectedActivities.length) {
      selectedActivities = ['content_marketing', 'email_marketing', 'social_media'];
    }

    console.log('InhouseMarketingResultsAdapter: Context data', {
      companySize,
      sectorName,
      selectedActivities: selectedActivities.length
    });

    // Step 4: Process insights from raw results or generate them algorithmically
    const rawInsights = rawResults.insights || [];
    let processedInsights = [];

    // Check for legitimate insights from the scoring engine
    if (rawInsights.length > 0) {
      // Use scoring engine insights, but ensure they're consistent and valid
      processedInsights = rawInsights.filter(insight => {
        return insight && typeof insight.text === 'string' && 
               (insight.type === 'strength' || insight.type === 'weakness');
      });
    }

    // If we have insufficient valid insights, generate them algorithmically based on scores
    if (processedInsights.length < 4) {
      console.log('InhouseMarketingResultsAdapter: Generating algorithmic insights based on scores');

      // Clear existing insights if they're sparse or invalid
      processedInsights = [];

      // Generate strengths based on highest dimension scores
      const strengths = [];

      if (validatedDimensions.humanReadiness > 50) {
        strengths.push({ 
          type: 'strength', 
          category: 'humanReadiness',
          text: 'Your team has good readiness for marketing transformation'
        });
      }

      if (validatedDimensions.technicalReadiness > 45) {
        strengths.push({ 
          type: 'strength', 
          category: 'technicalReadiness',
          text: 'Your technical foundation supports marketing automation'
        });
      }

      if (validatedDimensions.processMaturity > 45 || validatedDimensions.activityAutomation > 45) {
        strengths.push({ 
          type: 'strength', 
          category: 'processMaturity',
          text: 'Your marketing processes show good maturity'
        });
      }

      // Generate weaknesses based on lowest dimension scores
      const weaknesses = [];

      if (validatedDimensions.humanReadiness < 50) {
        weaknesses.push({ 
          type: 'weakness', 
          category: 'humanReadiness',
          text: 'Your team needs more preparation for marketing transformation'
        });
      }

      if (validatedDimensions.technicalReadiness < 45) {
        weaknesses.push({ 
          type: 'weakness', 
          category: 'technicalReadiness',
          text: 'Your technical foundation needs strengthening'
        });
      }

      if (validatedDimensions.processMaturity < 45 || validatedDimensions.activityAutomation < 45) {
        weaknesses.push({ 
          type: 'weakness', 
          category: 'processMaturity',
          text: 'Your marketing processes need more structure'
        });
      }

      // Add activity-specific insights based on selected activities
      if (selectedActivities.includes('content_marketing')) {
        const contentScore = activityScores.content_marketing || 35;
        if (contentScore > 50) {
          strengths.push({
            type: 'strength',
            category: 'content_marketing',
            text: 'Your content marketing foundation is solid'
          });
        } else {
          weaknesses.push({
            type: 'weakness',
            category: 'content_marketing',
            text: 'Your content marketing processes need improvement'
          });
        }
      }

      if (selectedActivities.includes('email_marketing')) {
        const emailScore = activityScores.email_marketing || 40;
        if (emailScore > 50) {
          strengths.push({
            type: 'strength',
            category: 'email_marketing',
            text: 'Your email marketing is well-structured'
          });
        } else {
          weaknesses.push({
            type: 'weakness',
            category: 'email_marketing',
            text: 'Your email marketing automation needs enhancement'
          });
        }
      }

      if (selectedActivities.includes('social_media')) {
        const socialScore = activityScores.social_media || 30;
        if (socialScore > 50) {
          strengths.push({
            type: 'strength',
            category: 'social_media',
            text: 'Your social media processes are effective'
          });
        } else {
          weaknesses.push({
            type: 'weakness',
            category: 'social_media',
            text: 'Your social media management needs streamlining'
          });
        }
      }

      // Combine strengths and weaknesses, ensuring we have at least 2 of each
      processedInsights = [
        ...strengths.slice(0, 3),
        ...weaknesses.slice(0, 3)
      ];

      // Ensure we have at least some insights
      if (processedInsights.length < 4) {
        if (strengths.length < 2) {
          processedInsights.push({
            type: 'strength',
            category: 'general',
            text: 'Your willingness to assess marketing maturity is a positive step'
          });
        }

        if (weaknesses.length < 2) {
          processedInsights.push({
            type: 'weakness',
            category: 'general',
            text: 'A comprehensive marketing strategy framework is needed'
          });
        }
      }
    }

    console.log('InhouseMarketingResultsAdapter: Processed insights', processedInsights.length);

    // Create filtered lists of strengths and weaknesses
    const strengthsList = processedInsights.filter(i => i.type === 'strength').map(i => i.text);
    const weaknessesList = processedInsights.filter(i => i.type === 'weakness').map(i => i.text);

    // Step 5: Generate priorities based on insights and selected activities
    let priorities = rawResults.priorities || [];

    // If priorities are missing or invalid, generate them algorithmically
    if (!priorities.length) {
      console.log('InhouseMarketingResultsAdapter: Generating algorithmic priorities');
      priorities = [];

      // Generate foundational priorities
      if (validatedDimensions.technicalReadiness < 50) {
        priorities.push({
          area: 'Technical Foundation',
          timeframe: 'short',
          impact: 'High',
          effort: 'Medium',
          steps: [
            'Audit existing marketing tools',
            'Identify integration opportunities',
            'Create implementation roadmap'
          ]
        });
      }

      if (validatedDimensions.humanReadiness < 50) {
        priorities.push({
          area: 'Team Development',
          timeframe: 'medium',
          impact: 'High',
          effort: 'Medium',
          steps: [
            'Assess skill gaps',
            'Create training program',
            'Establish learning workflow'
          ]
        });
      }

      // Add activity-specific priorities
      if (selectedActivities.includes('content_marketing')) {
        priorities.push({
          area: 'Content Marketing Efficiency',
          timeframe: 'short',
          impact: 'High',
          effort: 'Low',
          steps: [
            'Create content calendar',
            'Develop content templates',
            'Implement batch creation process'
          ]
        });
      }

      if (selectedActivities.includes('email_marketing')) {
        priorities.push({
          area: 'Email Automation',
          timeframe: 'short',
          impact: 'High',
          effort: 'Medium',
          steps: [
            'Segment audience',
            'Create welcome sequence',
            'Set up behavioral triggers'
          ]
        });
      }

      if (selectedActivities.includes('social_media')) {
        priorities.push({
          area: 'Social Media Management',
          timeframe: 'short',
          impact: 'Medium',
          effort: 'Low',
          steps: [
            'Create content batching system',
            'Set up scheduling calendar',
            'Implement engagement workflow'
          ]
        });
      }
    }

    console.log('Selected services/activities for recommendations:', selectedActivities);
    console.log('Sector context:', sector);

    // Format data in the structure expected by InhouseResultsView component
    // Generate readiness level headline based on overall score
    const getReadinessHeadline = (score) => {
      if (score < 40) return 'Urgent Action Required';
      if (score < 60) return 'Early Stage Adaptation';
      if (score < 80) return 'Building Strong Capability';
      return 'Marketing Transformation Ready';
    };

    // Build executive summary using the processed insights we generated earlier
    const executiveSummary = {
      headline: getReadinessHeadline(overallScore),
      subheadline: `Your organization scored ${overallScore} out of 100 on marketing readiness.`,
      summary: `Based on your assessment responses, we've analyzed your organization's readiness for in-house marketing transformation across key dimensions. Your overall score of ${overallScore} indicates ${overallScore < 50 ? 'significant opportunities for improvement' : 'a solid foundation with targeted improvement areas'}.`,
      strengths: processedInsights.filter(insight => insight.type === 'strength').map(s => s.text),
      weaknesses: processedInsights.filter(insight => insight.type === 'weakness').map(w => w.text)
    };

    console.log('InhouseMarketingResultsAdapter: Executive summary built with', {
      headline: executiveSummary.headline,
      strengthsCount: executiveSummary.strengths.length,
      weaknessesCount: executiveSummary.weaknesses.length
    });

    // Generate competitive analysis based on dimension scores and selected activities
    const competitiveAdvantages = [];
    const competitiveDisadvantages = [];

    // Add advantages based on dimension scores and selected activities
    if (validatedDimensions.humanReadiness > 50) {
      competitiveAdvantages.push('Team readiness for marketing transformation');
    }

    if (validatedDimensions.technicalReadiness > 50) {
      competitiveAdvantages.push('Technical foundation for marketing automation');
    }

    if (validatedDimensions.processMaturity > 50) {
      competitiveAdvantages.push('Mature marketing processes compared to peers');
    }

    // Add activity-specific advantages
    selectedActivities.forEach(activity => {
      const score = activityScores[activity] || 40;
      if (score > 50 && competitiveAdvantages.length < 5) {
        switch(activity) {
          case 'content_marketing':
            competitiveAdvantages.push('More developed content strategy than competitors');
            break;
          case 'email_marketing':
            competitiveAdvantages.push('More sophisticated email capabilities than industry average');
            break;
          case 'social_media':
            competitiveAdvantages.push('More consistent social media presence than peers');
            break;
          case 'seo_sem':
            competitiveAdvantages.push('More advanced search marketing approach than competitors');
            break;
          case 'analytics_data':
            competitiveAdvantages.push('Better data-driven marketing capabilities than industry average');
            break;
        }
      }
    });

    // Add disadvantages based on dimension scores
    if (validatedDimensions.humanReadiness < 45) {
      competitiveDisadvantages.push('Team marketing automation expertise below industry standard');
    }

    if (validatedDimensions.technicalReadiness < 45) {
      competitiveDisadvantages.push('Technical marketing infrastructure less developed than competitors');
    }

    if (validatedDimensions.processMaturity < 45) {
      competitiveDisadvantages.push('Marketing process maturity behind industry leaders');
    }

    if (validatedDimensions.activityAutomation < 45) {
      competitiveDisadvantages.push('Marketing automation adoption behind competitors');
    }

    // Ensure we have at least a few advantages and disadvantages
    if (competitiveAdvantages.length < 2) {
      competitiveAdvantages.push('Understanding of marketing transformation needs');
      competitiveAdvantages.push('Willingness to innovate marketing approach');
    }

    if (competitiveDisadvantages.length < 2) {
      competitiveDisadvantages.push('Limited cross-channel integration capabilities');
      competitiveDisadvantages.push('Data-driven marketing decision processes not fully implemented');
    }

    // Calculate appropriate industry benchmark based on company size and sector
    let industryBenchmark = 50; // Default benchmark

    // Adjust benchmark based on company size
    if (companySize === 'small') {
      industryBenchmark = 45;
    } else if (companySize === 'medium') {
      industryBenchmark = 55;
    } else if (companySize === 'large') {
      industryBenchmark = 60;
    }

    // Adjust benchmark based on sector if available
    const sectorString = typeof sectorName === 'string' ? sectorName.toLowerCase() : '';
    if (sectorString.includes('tech') || sectorString.includes('software') || sectorString.includes('saas')) {
      industryBenchmark += 10;
    } else if (sectorString.includes('retail') || sectorString.includes('ecommerce')) {
      industryBenchmark += 5;
    } else if (sectorString.includes('manufacturing') || sectorString.includes('industrial')) {
      industryBenchmark -= 5;
    }

    // Format market position data
    const marketPosition = {
      position: getMaturityLevel(overallScore),
      description: `Your organization is in the ${getMaturityLevel(overallScore).toLowerCase()} stage of marketing maturity.`,
      competitiveAnalysis: {
        ahead: competitiveAdvantages,
        behind: competitiveDisadvantages
      },
      industryBenchmark: {
        score: Math.min(95, Math.max(30, industryBenchmark)),
        comparison: overallScore > industryBenchmark ? 'ahead' : 'behind'
      }
    };

    console.log('InhouseMarketingResultsAdapter: Market position assessment', {
      position: marketPosition.position,
      industryBenchmark: marketPosition.industryBenchmark.score,
      comparison: marketPosition.industryBenchmark.comparison
    });

    // Service-specific recommendation mappings
    const serviceRecommendations = {
      content_marketing: {
        quick: {
          title: 'Implement Content Calendar',
          description: 'Organize your content production pipeline with a structured calendar',
          impact: 'High',
          effort: 'Low',
          implementation: ['Audit existing content', 'Define content themes', 'Create 90-day calendar']
        },
        medium: 'Develop content repurposing workflow',
        long: 'Implement AI-assisted content creation'
      },
      email_marketing: {
        quick: {
          title: 'Set Up Welcome Sequence',
          description: 'Create an automated onboarding email series for new subscribers',
          impact: 'High',
          effort: 'Medium',
          implementation: ['Map customer journey', 'Draft 5-email sequence', 'Implement automation triggers']
        },
        medium: 'Segment email list based on behavior',
        long: 'Implement predictive send-time optimization'
      },
      social_media: {
        quick: {
          title: 'Create Content Batching Process',
          description: 'Implement efficient process for creating social posts in batches',
          impact: 'Medium',
          effort: 'Low',
          implementation: ['Set up content templates', 'Schedule monthly batching sessions', 'Use scheduling tools']
        },
        medium: 'Develop cross-platform content strategy',
        long: 'Implement social listening and response system'
      },
      seo_sem: {
        quick: {
          title: 'Conduct Keyword Gap Analysis',
          description: 'Identify untapped keyword opportunities vs competitors',
          impact: 'High',
          effort: 'Medium',
          implementation: ['Analyze top 3 competitors', 'Identify content gaps', 'Prioritize quick-win terms']
        },
        medium: 'Implement technical SEO improvements',
        long: 'Create hub and spoke content strategy'
      },
      analytics_data: {
        quick: {
          title: 'Set Up Basic Dashboard',
          description: 'Create a centralized view of key marketing metrics',
          impact: 'Medium',
          effort: 'Low',
          implementation: ['Define KPIs', 'Connect data sources', 'Build initial dashboard']
        },
        medium: 'Implement campaign attribution model',
        long: 'Develop predictive analytics capabilities'
      },
      paid_advertising: {
        quick: {
          title: 'Audit Ad Account Structure',
          description: 'Reorganize campaigns for better tracking and optimization',
          impact: 'Medium',
          effort: 'Low',
          implementation: ['Review current structure', 'Create logical hierarchy', 'Implement proper tagging']
        },
        medium: 'Implement cross-channel retargeting',
        long: 'Develop AI-driven bidding strategy'
      },
      creative_design: {
        quick: {
          title: 'Create Brand Style Guide',
          description: 'Document visual standards for consistent brand presentation',
          impact: 'Medium',
          effort: 'Low',
          implementation: ['Audit current assets', 'Document standards', 'Create template library']
        },
        medium: 'Implement design workflow system',
        long: 'Develop modular design system'
      },
      marketing_automation: {
        quick: {
          title: 'Map Automation Opportunities',
          description: 'Identify and prioritize processes for automation',
          impact: 'High',
          effort: 'Low',
          implementation: ['Document manual processes', 'Prioritize by impact/effort', 'Select initial project']
        },
        medium: 'Implement cross-channel triggers',
        long: 'Create full customer journey automation'
      }
    };

    // Generate service-specific quick wins based on selected services
    const quickWins = selectedActivities.length > 0 
      ? selectedActivities
        .filter(service => serviceRecommendations[service])
        .map(service => serviceRecommendations[service].quick)
        .filter(Boolean)
        .slice(0, 3)
      : priorities.filter(p => p.timeframe === 'short').map(p => ({
          title: p.title || 'Implement basic automation',
          description: p.description || 'Quick impact opportunity requiring minimal investment',
          impact: p.impact || 'Medium',
          effort: p.effort || 'Low',
          implementation: p.steps || ['Identify process', 'Select tool', 'Initial setup']
        })) || [
          {
            title: 'Implement basic email automation',
            description: 'Set up fundamental automated email sequences',
            impact: 'Medium',
            effort: 'Low',
            implementation: ['Audit current emails', 'Select automation triggers', 'Build first sequence']
          },
          {
            title: 'Enhance analytics tracking',
            description: 'Improve measurement of key marketing activities',
            impact: 'High',
            effort: 'Medium',
            implementation: ['Audit current tracking', 'Define key metrics', 'Implement tracking']
          }
        ];

    // Generate service-specific medium and long-term recommendations
    const mediumTermActions = selectedActivities.length > 0 
      ? selectedActivities
          .filter(service => serviceRecommendations[service])
          .map(service => serviceRecommendations[service].medium)
          .filter(Boolean)
          .slice(0, 4)
      : priorities.filter(p => p.timeframe === 'medium').map(p => p.title) || 
          ['Expand automation scope', 'Implement cross-channel coordination', 'Enhance reporting'];

    const longTermActions = selectedActivities.length > 0 
      ? selectedActivities
          .filter(service => serviceRecommendations[service])
          .map(service => serviceRecommendations[service].long)
          .filter(Boolean)
          .slice(0, 4)
      : priorities.filter(p => p.timeframe === 'long').map(p => p.title) || 
          ['Implement advanced personalization', 'Predictive analytics integration', 'Full marketing operations optimization'];

    // Sector-specific investment guidelines
    const sectorInvestmentGuidelines = {
      b2b_saas: 'Estimated investment of $30,000-60,000 with 4-6x ROI potential within 12 months',
      ecommerce: 'Estimated investment of $25,000-55,000 with 3-5x ROI potential within 9 months',
      professional_services: 'Estimated investment of $20,000-45,000 with 2-4x ROI potential within 12 months',
      healthcare: 'Estimated investment of $35,000-70,000 with 2-3x ROI potential within 15 months',
      manufacturing: 'Estimated investment of $25,000-50,000 with 2-4x ROI potential within 12 months'
    };

    // Strategic roadmap with phases - now with service-specific recommendations
    const strategicRoadmap = {
      overview: 'A 9-month phased approach to transform your marketing capabilities',
      phase1: {
        title: 'Foundation Building',
        timeframe: '0-3 months',
        focus: 'Establish fundamental processes and quick wins',
        actions: quickWins.map(win => win.title),
        expectedOutcome: 'Baseline processes established with initial efficiency gains'
      },
      phase2: {
        title: 'Capability Expansion',
        timeframe: '3-6 months',
        focus: 'Scale successful initiatives and add complexity',
        actions: mediumTermActions,
        expectedOutcome: 'Improved cross-channel effectiveness with better measurement'
      },
      phase3: {
        title: 'Advanced Optimization',
        timeframe: '6-9 months',
        focus: 'Refine and optimize for maximum impact',
        actions: longTermActions,
        expectedOutcome: 'Highly optimized, data-driven marketing operation'
      },
      investmentGuide: savingsPotential.description || 
        (sector.id && sectorInvestmentGuidelines[sector.id]) || 
        'Estimated investment of $20,000-50,000 with 3-5x ROI potential within 12 months'
    };

    // Service-specific expected outcomes
    const serviceOutcomes = {
      content_marketing: {
        area: 'Content Effectiveness',
        keyMetric: 'Content Engagement Rate',
        baseline: 'Current baseline',
        target: '45% increase',
        timeframe: '6 months'
      },
      email_marketing: {
        area: 'Email Performance',
        keyMetric: 'Email Conversion Rate',
        baseline: 'Current baseline',
        target: '30% increase',
        timeframe: '3 months'
      },
      social_media: {
        area: 'Social Engagement',
        keyMetric: 'Social Media Conversion',
        baseline: 'Current baseline',
        target: '40% increase',
        timeframe: '6 months'
      },
      seo_sem: {
        area: 'Organic Traffic',
        keyMetric: 'Qualified Organic Visitors',
        baseline: 'Current baseline',
        target: '50% increase',
        timeframe: '9 months'
      },
      analytics_data: {
        area: 'Decision Quality',
        keyMetric: 'Data-Driven Decisions',
        baseline: '20% of decisions',
