import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styles from './ServiceSelector.module.css';
import { useAssessment } from '../hooks/useAssessment';

const ServiceSelector = () => {
  const { type } = useParams();
  const navigate = useNavigate();
  const { assessmentData, updateAssessmentData, saveProgress } = useAssessment(type);

  const [services, setServices] = useState([]);
  const [selectedServices, setSelectedServices] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [loadingRetries, setLoadingRetries] = useState(0);

  // Initialize component from localStorage directly for reliability
  useEffect(() => {
    try {
      // Always check localStorage directly first
      const savedDataString = localStorage.getItem('assessmentData');
      if (savedDataString) {
        const savedData = JSON.parse(savedDataString);
        console.log('DIRECT LOCALSTORAGE READ:', savedData);

        // If we have qualifying data in localStorage but not in assessmentData, it means 
        // the hook's state hasn't updated yet, so we'll manually update it
        if (savedData.qualifying && Object.keys(savedData.qualifying).length > 0 && 
            (!assessmentData.qualifying || Object.keys(assessmentData.qualifying).length === 0)) {
          console.log('Manually updating assessment data from localStorage');
          updateAssessmentData(savedData);
        }

        // Initialize selected services from saved data
        if (savedData.services && savedData.services.length > 0) {
          const initialSelections = {};
          savedData.services.forEach(serviceId => {
            initialSelections[serviceId] = 1; // Set to 1 to indicate selected
          });
          setSelectedServices(initialSelections);
        }
      }
    } catch (error) {
      console.error('Error reading from localStorage:', error);
    }

    // Check if qualifying data exists in localStorage or assessmentData
    checkQualifyingData();
  }, []);

  // Function to check if qualifying data exists and redirect if needed
  const checkQualifyingData = () => {
    console.log('Checking qualifying data, assessment type:', type);
    console.log('Current assessment data:', assessmentData);

    let hasQualifyingData = false;

    // Check assessment data from hook
    if (assessmentData?.qualifying && Object.keys(assessmentData.qualifying).length > 0) {
      hasQualifyingData = true;
    }

    // If not in hook state, check localStorage directly
    if (!hasQualifyingData) {
      try {
        const savedDataString = localStorage.getItem('assessmentData');
        if (savedDataString) {
          const savedData = JSON.parse(savedDataString);
          if (savedData.qualifying && Object.keys(savedData.qualifying).length > 0) {
            console.log('Found qualifying data in localStorage');
            hasQualifyingData = true;
            // Update assessment data
            updateAssessmentData(savedData);
          }
        }
      } catch (error) {
        console.error('Error checking localStorage for qualifying data:', error);
      }
    }

    // If still no qualifying data, redirect to qualifying questions
    if (!hasQualifyingData) {
      console.log('No qualifying data found, redirecting to qualifying questions');
      navigate(`/assessment/${type}/qualifying`, { replace: true });
      return false;
    }

    // If we have qualifying data, load services
    loadServices();
    return true;
  };

    const description = isAgency ?
        "Drag the sliders to show relative importance. Don't worry about exact percentages—we'll figure that out." :
        "Select all that apply - we'll ask about each one.";

    return (
        <div className={styles.screen}>
            <div className={styles.header}>
                <div className={styles.logo}>obsolete<span>.</span></div>
                <ProgressBar progress={45} label="Step 4 of 7" />
            </div>

            <h2>{title}</h2>
            <p>{description}</p>

            {loading ? (
                <p style={{ color: '#aaa', fontStyle: 'italic' }}>Loading services...</p>
            ) : (
                <div className={styles.servicesGrid}>
                    {services.map((service) => (
                        <div key={service.id} className={styles.serviceItem}>
                            <div className={styles.serviceRow}>
                                <div className={styles.serviceIcon}>{service.icon}</div>
                                <div className={styles.serviceContent}>
                                    <div className={styles.serviceName}>{service.name}</div>
                                    <div className={styles.serviceDescription}>{service.description}</div>
                                </div>
                            </div>
                            {isAgency ? (
                                <div className={styles.sliderContainer}>
                                    <input
                                        type="range"
                                        min="0"
                                        max="100"
                                        value={selectedServices[service.id] || 0}
                                        onChange={(e) => handleSliderChange(service.id, e.target.value)}
                                        className={styles.rangeSlider}
                                    />
                                    <span className={styles.sliderValue}>
                                        {selectedServices[service.id] || 0}%
                                    </span>
                                </div>
                            ) : (
                                <div className={styles.checkboxContainer}>
                                    <input
                                        type="checkbox"
                                        id={service.id}
                                        checked={!!selectedServices[service.id]}
                                        onChange={(e) => handleSliderChange(service.id, e.target.checked ? 1 : 0)}
                                        className={styles.checkbox}
                                    />
                                    <label htmlFor={service.id} className={styles.checkboxLabel}></label>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}

            <div className={styles.coCreationHighlight}>
                <p>Why we ask: Knowing your core {isAgency ? 'services' : 'activities'} helps us pinpoint where AI can have the biggest impact on your efficiency and {isAgency ? 'client value' : 'results'}.</p>
            </div>

            {error && (
                <div className={styles.errorContainer}>
                    <p>{error}</p>
                    <button 
                        className={styles.navButton} 
                        onClick={() => window.location.reload()}
                    >
                        Try Again
                    </button>
                </div>
            )}

            <div className={styles.navigation}>
                <button className={styles.navButton} onClick={() => navigate(`/assessment/${type}/qualify`, { replace: true })}>← Back</button>
                <button 
                    className={`${styles.navButton} ${styles.primary}`}
                    disabled={!hasSelections}
                    onClick={handleContinue}
                >
                    {!hasSelections ? 'Please select a service' : 'Continue'}
                </button>
            </div>
        </div>
    );
};

export default ServiceSelector;
