import React, { useState, useEffect } from 'react';
import { ProgressBar } from './ProgressBar';
import styles from '../styles/components.module.css';

// Import JSON data directly for webpack to bundle correctly
import agencyServices from '../../assessments/agency-vulnerability/services.json';
import inhouseActivities from '../../assessments/inhouse-marketing/activities.json';

const ServiceSelector = ({ assessmentType, onSelect, onBack }) => {
    console.log('==== LOADED VERSION: /src/core/components/ServiceSelector.jsx ====');
    const [services, setServices] = useState([]);
    const [selectedServices, setSelectedServices] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    console.log(`ServiceSelector rendered, assessment type: ${assessmentType}`);
    console.log('Current assessment data:', { qualifying: {}, services: services || [], responses: {} });

    const loadServices = async () => {
        try {
            setLoading(true);
            console.log(`Loading services for ${assessmentType}`);
            // Dynamic import for services based on assessment type - fix path to be relative to src
            console.log('Attempting to load from path:', `../../assessments/${assessmentType}/services.json`);
            const servicesModule = await import(`../../assessments/${assessmentType}/services.json`);
            // Ensure services is always an array
            const servicesData = servicesModule.default || {};
            const formattedServices = Array.isArray(servicesData) ? servicesData : 
                Object.values(servicesData);

            console.log('Services loaded:', formattedServices);

            if (formattedServices && formattedServices.length > 0) {
                setServices(formattedServices);
                setError(null);
            } else {
                console.error('Services data is empty or not in expected format:', servicesData);
                setError('No services found for this assessment type.');
            }
        } catch (error) {
            console.error('Failed to load services:', error);
            setError(`Failed to load services: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        loadServices();
    }, [assessmentType]);

    const toggleService = (serviceId) => {
        setSelectedServices(prev => {
            const newSelections = { ...prev };
            if (newSelections[serviceId]) {
                delete newSelections[serviceId];
            } else {
                newSelections[serviceId] = 1;
            }
            return newSelections;
        });
    };

    const handleContinue = () => {
        const selected = Object.keys(selectedServices);

        if (selected.length === 0) {
            alert('Please select at least one service to continue');
            return;
        }

        onSelect(selected);
    };

    const hasSelections = Object.keys(selectedServices).length > 0;

    if (loading) {
        return <div className={styles.loading}>Loading services...</div>;
    }

    if (error) {
        return (
            <div className={styles.error}>
                <p>{error}</p>
                <button onClick={() => loadServices()}>Try Again</button>
            </div>
        );
    }

    return (
        <div className={styles.container}>
            <div className={styles.header}>
                <h2>Select Services</h2>
                <ProgressBar progress={40} />
            </div>

            <p>Which services does your agency provide? Select all that apply.</p>

            <div className={styles.serviceGrid}>
                {services.map(service => (
                    <div 
                        key={service.id}
                        className={`${styles.serviceCard} ${selectedServices[service.id] ? styles.selected : ''}`}
                        onClick={() => toggleService(service.id)}
                    >
                        <div className={styles.serviceIcon}>{service.icon}</div>
                        <h3>{service.name}</h3>
                        <p>{service.description}</p>
                        {selectedServices[service.id] && <div className={styles.checkmark}>✓</div>}
                    </div>
                ))}
            </div>

            <div className={styles.buttonContainer}>
                <button 
                    className={styles.backButton} 
                    onClick={onBack}
                >
                    Back
                </button>
                <button 
                    className={styles.continueButton}
                    disabled={!hasSelections}
                    onClick={handleContinue}
                >
                    Continue
                </button>
            </div>
        </div>
    );
};

export default ServiceSelector;
