import React, { useState, useEffect } from 'react';
import { ProgressBar } from './ProgressBar';
import styles from '../styles/components.module.css';

// Import JSON data directly for webpack to bundle correctly
import agencyServices from '../../assessments/agency-vulnerability/services.json';
import inhouseActivities from '../../assessments/inhouse-marketing/activities.json';

const ServiceSelector = ({ assessmentType, onSelect, onBack }) => {
    console.log('==== LOADED VERSION: /src/core/components/ServiceSelector.jsx ====');
    const [services, setServices] = useState([]);
    const [allocations, setAllocations] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    console.log(`ServiceSelector rendered, assessment type: ${assessmentType}`);

    // Load services data based on assessment type
    const loadServices = () => {
        try {
            setLoading(true);
            console.log(`Loading services for ${assessmentType}`);

            // Use the statically imported JSON data based on assessment type
            let servicesData;
            if (assessmentType === 'agency-vulnerability') {
                console.log('Using agency services data');
                servicesData = agencyServices;
            } else if (assessmentType === 'inhouse-marketing') {
                console.log('Using inhouse activities data');
                servicesData = inhouseActivities;
            } else {
                throw new Error(`Unknown assessment type: ${assessmentType}`);
            }

            // Extract the correct data array
            const serviceArray = assessmentType === 'agency-vulnerability'
                ? (servicesData.services || [])
                : (servicesData.activities || []);

            console.log('Services data loaded:', serviceArray);

            if (serviceArray.length > 0) {
                setServices(serviceArray);

                // Initialize allocations with 0 for each service
                const initialAllocations = {};
                serviceArray.forEach(service => {
                    initialAllocations[service.id] = 0;
                });
                setAllocations(initialAllocations);

                setError(null);
            } else {
                console.error('Service data is empty:', servicesData);
                setError('No services found for this assessment type.');
            }
        } catch (error) {
            console.error('Failed to load services:', error);
            setError(`Failed to load services: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(() => {
        loadServices();
    }, [assessmentType]);

    // Handle slider value change
    const handleSliderChange = (serviceId, value) => {
        // Parse the value to ensure it's a number
        const newValue = parseInt(value, 10) || 0;

        // Calculate the difference from previous allocation
        const oldValue = allocations[serviceId] || 0;
        const difference = newValue - oldValue;

        // Update the allocations and total
        setAllocations(prev => ({
            ...prev,
            [serviceId]: newValue
        }));

        setTotalAllocated(prev => prev + difference);
    };

    // Calculate the percentage of total allocated
    const percentageAllocated = totalAllocated;
    const isFullyAllocated = percentageAllocated === 100;
    const isOverAllocated = percentageAllocated > 100;

    // Helper to get remaining percentage that can be allocated
    const getRemainingPercentage = () => {
        return Math.max(0, 100 - totalAllocated);
    };

    // Handle continue button click
    const handleContinue = () => {
        // Only services with allocation > 0 should be included
        const selectedServicesWithAllocation = Object.entries(allocations)
            .filter(([_, value]) => value > 0)
            .reduce((acc, [key, value]) => {
                acc[key] = value / 100; // Convert to decimal proportion
                return acc;
            }, {});

        if (Object.keys(selectedServicesWithAllocation).length === 0) {
            alert('Please allocate percentage to at least one service to continue.');
            return;
        }

        if (!isFullyAllocated) {
            if (!window.confirm(`You've only allocated ${percentageAllocated}% of your resources. Are you sure you want to continue?`)) {
                return;
            }
        }

        onSelect(selectedServicesWithAllocation);
    };

    // Reset all allocations
    const resetAllocations = () => {
        const resetValues = {};
        services.forEach(service => {
            resetValues[service.id] = 0;
        });
        setAllocations(resetValues);
        setTotalAllocated(0);
    };

    // Even distribution button
    const distributeEvenly = () => {
        if (services.length === 0) return;

        const equalValue = Math.floor(100 / services.length);
        const remainder = 100 - (equalValue * services.length);

        const newAllocations = {};
        services.forEach((service, index) => {
            // Add the remainder to the first service
            newAllocations[service.id] = equalValue + (index === 0 ? remainder : 0);
        });

        setAllocations(newAllocations);
        setTotalAllocated(100);
    };

    if (loading) {
        return (
            <div className={styles.loadingContainer}>
                <div className={styles.spinner}></div>
                <p>Loading services...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className={styles.errorContainer}>
                <h3>Error</h3>
                <p>{error}</p>
                <button onClick={loadServices} className={styles.button}>Retry</button>
                <button onClick={onBack} className={styles.buttonSecondary}>Go Back</button>
            </div>
        );
    }

    return (
        <div className={styles.container}>
            <h2>{assessmentType === 'agency-vulnerability' ? 'Select Your Services' : 'Select Your Activities'}</h2>
            <p>Please allocate percentages to represent how your resources are distributed:</p>

            <div className={styles.allocationHeader}>
                <div className={styles.allocationTotal}>
                    <span>Total Allocated: </span>
                    <span className={isOverAllocated ? styles.overAllocated : isFullyAllocated ? styles.fullyAllocated : ''}>
                        {percentageAllocated}%
                    </span>
                    <div className={styles.allocationBar}>
                        <div 
                            className={`${styles.allocationBarFill} ${isOverAllocated ? styles.overAllocated : ''}`}
                            style={{ width: `${Math.min(percentageAllocated, 100)}%` }}
                        ></div>
                    </div>
                </div>
                <div className={styles.allocationControls}>
                    <button onClick={resetAllocations} className={styles.buttonSmall}>Reset</button>
                    <button onClick={distributeEvenly} className={styles.buttonSmall}>Distribute Evenly</button>
                </div>
            </div>

            <div className={styles.serviceList}>
                {services.map(service => {
                    const currentAllocation = allocations[service.id] || 0;
                    const maxAllocation = currentAllocation + getRemainingPercentage();

                    return (
                        <div key={service.id} className={styles.serviceItem}>
                            <div className={styles.serviceHeader}>
                                <span className={styles.serviceName}>{service.name}</span>
                                <span className={styles.serviceAllocation}>{currentAllocation}%</span>
                            </div>

                            {service.description && (
                                <p className={styles.serviceDescription}>{service.description}</p>
                            )}

                            <div className={styles.sliderContainer}>
                                <input
                                    type="range"
                                    min="0"
                                    max="100"
                                    value={currentAllocation}
                                    onChange={(e) => handleSliderChange(service.id, e.target.value)}
                                    className={styles.slider}
                                    disabled={currentAllocation === 0 && maxAllocation === 0}
                                />
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className={styles.navigationButtons}>
                <button onClick={onBack} className={styles.buttonSecondary}>
                    Back
                </button>
                <button 
                    onClick={handleContinue} 
                    className={styles.button}
                    disabled={isOverAllocated}
                >
                    Continue
                </button>
            </div>

            <ProgressBar currentStep={2} totalSteps={5} />
        </div>
    );
};

export default ServiceSelector;
