import React, { useState, useEffect } from 'react';
import { QuestionCard } from './QuestionCard';
import { ProgressBar } from './ProgressBar';
import styles from '../styles/components.module.css';

const DynamicQuestions = ({ 
    assessmentType, 
    saveResponse, 
    getResponse, 
    onComplete,
    onBack,
    progress
}) => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        loadQuestions();
    }, [assessmentType]);

    const loadQuestions = async () => {
        try {
            setLoading(true);
            console.log('Loading questions for assessment type:', assessmentType);

            // Load core questions based on assessment type
            const module = await import(`../../assessments/${assessmentType}/questions.json`);
            const data = module.default || module;

            // Extract and flatten core questions array from the loaded data
            let coreQuestions = [];
            if (data.questions) {
                // Direct questions array format
                coreQuestions = data.questions;
            } else if (data.coreQuestions) {
                // Nested questions by category format
                Object.values(data.coreQuestions).forEach(questionSet => {
                    if (Array.isArray(questionSet)) {
                        coreQuestions = [...coreQuestions, ...questionSet];
                    }
                });
            }
            console.log(`Loaded ${coreQuestions.length} core questions`);

            // If we have selected services, load service-specific questions
            let serviceQuestions = [];
            // Get selected services from responses
            const selectedServices = getResponse('selectedServices');

            console.log('Selected services:', selectedServices);

            if (selectedServices && Object.keys(selectedServices).length > 0) {
                try {
                    // Load service-specific questions
                    const serviceModule = await import(`../../assessments/${assessmentType}/service-questions.json`);
                    const serviceData = serviceModule.default || serviceModule;

                    // Check threshold for including service questions (any service with allocation > 0.1 or 10%)
                    Object.keys(selectedServices).forEach(serviceId => {
                        const allocation = selectedServices[serviceId];
                        // Only include questions for services with significant allocation (> 10%)
                        if (allocation > 0.1) {
                            if (serviceData.serviceQuestions && serviceData.serviceQuestions[serviceId]) {
                                const serviceQuestionSet = serviceData.serviceQuestions[serviceId].questions || [];
                                console.log(`Adding ${serviceQuestionSet.length} questions for service ${serviceId} (allocation: ${Math.round(allocation * 100)}%)`);
                                serviceQuestions = [...serviceQuestions, ...serviceQuestionSet];
                            } else {
                                console.log(`No questions found for service ${serviceId}`);
                            }
                        } else {
                            console.log(`Skipping questions for service ${serviceId} due to low allocation (${Math.round(allocation * 100)}%)`);
                        }
                    });
                } catch (err) {
                    console.error('Error loading service questions:', err);
                }
            }

            console.log(`Total service questions: ${serviceQuestions.length}`);

            // Combine all question types
            const allQuestions = [
                ...coreQuestions,
                ...serviceQuestions
            ];

            console.log(`Total questions to display: ${allQuestions.length}`);
            setQuestions(allQuestions);
        } catch (error) {
            console.error('Failed to load questions:', error);
        } finally {
            setLoading(false);
        }
    };

    const currentQuestion = questions[currentQuestionIndex];
    const isLastQuestion = currentQuestionIndex === questions.length - 1;

    const handleAnswer = (value, option) => {
        if (currentQuestion) {
            saveResponse(currentQuestion.id, value);
        }
    };

    const handleNext = () => {
        if (isLastQuestion) {
            // Move to the next stage using onComplete callback
            onComplete();
        } else {
            setCurrentQuestionIndex(prev => prev + 1);
        }
    };

    const handlePrevious = () => {
        if (currentQuestionIndex > 0) {
            setCurrentQuestionIndex(prev => prev - 1);
        } else {
            // Go back to the previous stage in the assessment flow
            onBack();
        }
    };

    const handleSkip = () => {
        handleNext();
    };

    if (loading) {
        return (
            <div className={styles.loadingContainer}>
                <div className={styles.spinner} />
                <p>Loading questions...</p>
            </div>
        );
    }

    if (!currentQuestion) {
        return (
            <div className={styles.errorContainer}>
                <p>No questions available for this assessment.</p>
                <button onClick={onBack}>Go Back</button>
            </div>
        );
    }

    const selectedValue = getResponse(currentQuestion.id);

    return (
        <div className={styles.screen}>
            <div className={styles.header}>
                <div className={styles.logo}>obsolete<span>.</span></div>
                <ProgressBar progress={progress || 65} label="Step 5 of 7" />
            </div>
            <QuestionCard
                question={currentQuestion}
                questionNumber={currentQuestionIndex + 1}
                totalQuestions={questions.length}
                selectedValue={selectedValue}
                onSelect={handleAnswer}
                insight={currentQuestion.insight}
                benchmark={currentQuestion.benchmark}
            />

            <div className={styles.navigation}>
                <button className={styles.navButton} onClick={handlePrevious}>
                    ← Back
                </button>
                <button className={`${styles.navButton} ${styles.skip}`} onClick={handleSkip}>
                    Skip
                </button>
                <button 
                    className={`${styles.navButton} ${styles.primary}`}
                    disabled={!selectedValue}
                    onClick={handleNext}
                >
                    {isLastQuestion ? 'Complete' : 'Continue'}
                </button>
            </div>
        </div>
    );
};

export default DynamicQuestions;
