import React, { useState, useEffect } from 'react';
import styles from '../styles/components.module.css';
import { AssessmentEngine } from '../engine/AssessmentEngine';

const ResultsDashboard = ({ 
  assessmentType, 
  calculateResults, 
  onRestart,
  ResultsView,
  getResponse, // Add these props to access the full assessment data
  getContext
}) => {

  const [results, setResults] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeSection, setActiveSection] = useState('summary');

  useEffect(() => {
    console.log('ResultsDashboard mounted with assessmentType:', assessmentType);
    console.log('Custom ResultsView provided:', !!ResultsView);
    generateResults();
  }, []);

  // Transform the scoring engine output to match the expected ResultsView format
  const adaptResultsFormat = (rawResults) => {
    console.log('Adapting results format for view:', assessmentType);
    console.log('Raw results structure:', Object.keys(rawResults || {}));

    if (!rawResults) {
      console.error('adaptResultsFormat received null/undefined rawResults');
      return null;
    }

    // For agency-vulnerability assessment
    if (assessmentType === 'agency-vulnerability') {
      // Direct extraction from raw results - the scoring engine output structure
      // doesn't have a nested 'scores' object
      const overallScore = rawResults.overall || 0;
      const dimensionScores = rawResults.dimensions || {};

      // Extract insights which contain the priority areas
      const insights = rawResults.insights || [];
      const priorities = insights.filter(insight => insight.priority).sort((a, b) => a.priority - b.priority);

      // Create these objects with default values if not present in raw results
      const vulnerabilityMetrics = rawResults.vulnerabilityMetrics || {};
      const serviceScores = rawResults.serviceScores || {};

      // Log the correctly extracted data
      console.log('Correctly extracted data from raw results:', {
        overallScore,
        dimensionScores,
        prioritiesCount: priorities.length,
        serviceScoresCount: Object.keys(serviceScores).length,
        vulnerabilityMetrics
      });

      // Construct default market position data if not available
      const marketPosition = {
        position: overallScore < 40 ? 'Behind Competitors' : overallScore < 70 ? 'On Par with Competitors' : 'Leading the Market',
        percentile: Math.round(overallScore * 0.9), // Approximate percentile based on score
        timeAdvantage: overallScore < 40 ? '1-3 months behind' : '3-6 months advantage',
        competitiveAdvantage: overallScore < 40 ? 'Limited' : overallScore < 70 ? 'Moderate' : 'Significant',
        threatLevel: overallScore < 40 ? 'High' : overallScore < 70 ? 'Moderate' : 'Low'
      };

      // Construct default efficiency data if not available
      const efficiencyPotential = {
        overall: Math.round(vulnerabilityMetrics.overall || 50),
        timeRecovered: `${Math.round((vulnerabilityMetrics.average || 50) / 10)} hours/week`,
        costSavings: `${Math.round((vulnerabilityMetrics.overall || 50) * 100 / 25)}k annually`
      };

      // Create adapted results format expected by AgencyResultsView
      const adaptedResults = {
        // Executive summary section
        executive: {
          headline: `Your Agency is ${marketPosition.position}`,
          subheadline: `You're in the ${marketPosition.percentile}th percentile of agencies`,
          keyMetrics: {
            readinessScore: {
              value: overallScore,
              label: 'Readiness Score',
              interpretation: `${overallScore < 40 ? 'Low' : overallScore < 70 ? 'Medium' : 'High'} readiness`
            },
            efficiencyGain: {
              value: `${efficiencyPotential.overall}%`,
              label: 'Efficiency Potential',
              interpretation: `${efficiencyPotential.timeRecovered} hours/week reclaimed`
            },
            transformationUrgency: {
              value: priorities.length > 0 ? (priorities[0].urgency || 'Medium') : 'Medium',
              label: 'Transformation Urgency',
              interpretation: marketPosition.timeAdvantage
            }
          },
          narrative: `Your agency's AI readiness score is ${overallScore}. ${marketPosition.competitiveAdvantage} competitive advantage with ${marketPosition.threatLevel} threat level.`
        },

        // Readiness analysis section
        readiness: {
          dimensions: Object.entries(dimensionScores).map(([key, value]) => ({
            name: key.charAt(0).toUpperCase() + key.slice(1),
            score: Math.round(value),
            description: `Your ${key} score indicates ${value < 40 ? 'significant room for improvement' : value < 70 ? 'moderate capability' : 'strong capability'}.`
          })),
          narrative: `Your agency shows ${overallScore < 40 ? 'concerning vulnerabilities' : overallScore < 70 ? 'moderate preparedness' : 'strong positioning'} in the face of AI disruption. The dimension scores indicate where to focus your transformation efforts.`,
          serviceReadiness: Object.entries(serviceScores).map(([service, data]) => ({
            name: service.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
            score: data?.score || 0,
            interpretation: data?.readiness || 'medium',
            timeToDisruption: data?.score > 70 ? '3-6 months' : data?.score > 40 ? '12-18 months' : '24+ months'
          })),
          transformationPaths: {
            content_creation: "Implement AI writing workflow: Research → AI Draft → Human Edit → AI Optimize. Target: 3x output, same team.",
            creative_design: "AI for ideation and variations: Brief → AI Concepts → Human Curation → AI Production. Deliver 10x options in half the time.",
            digital_marketing: "Automate optimization: AI for copy testing, bid management, audience discovery. Focus humans on strategy and client relations.",
            seo_sem: "AI-powered SEO at scale: Automated audits, content optimization, competitive tracking. Deliver enterprise-level insights to SMBs.",
            pr_comms: "AI for media intelligence: Monitor everything, predict pickup, personalize pitches. Humans focus on relationships and narrative.",
            strategy_consulting: "AI-enhanced insights: Use AI for research, analysis, and scenario modeling. Position as 'data-driven strategy' premium service."
          }
        },

        // Opportunities section
        opportunities: {
          immediate: {
            title: 'Priority Transformation Areas',
            subtitle: 'These areas require immediate attention',
            items: priorities.slice(0, 3).map(priority => ({
              title: priority.area || 'Unknown',
              description: priority.description || 'No description available',
              effort: priority.effort || 'Medium',
              impact: priority.impact || 'High'
            }))
          },
          // Add service-specific opportunities
          serviceSpecific: Object.entries(serviceScores)
            .filter(([_, data]) => data?.score > 50) // Focus on vulnerable services
            .map(([service, data]) => {
              // Map service key to proper format for lookup
              const serviceKey = service.toLowerCase().replace(/\s+/g, '_');

              // Create default recommendation if specific one not found
              return {
                service: service.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
                title: `AI-Enable ${service.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}`,
                description: `Implement AI tools to transform your ${service.replace(/_/g, ' ')} service, improving efficiency and output quality.`,
                difficulty: data?.score > 70 ? 'high' : 'medium',
                time: `Save ${Math.round(data?.score / 10)} hours/week`,
                impact: 'Significant productivity gain'
              };
            }),
          // Add strategic opportunities
          strategic: [
            {
              title: "AI-First Service Revolution",
              description: "Redesign all services with AI at the core. Not 'AI-enhanced' but 'AI-native'. Deliver 10x value at premium prices.",
              timeline: "6-month transformation",
              impact: "Market leadership position",
              complexity: "high"
            },
            {
              title: "Productize Your Expertise",
              description: "Transform your expertise into AI-powered tools. Package and sell your process as a SaaS offering.",
              timeline: "9-month project",
              impact: "Recurring revenue stream",
              complexity: "high"
            }
          ],
          // Valuation-enhancing opportunities
          valuationImpact: [
            {
              action: "Improve revenue predictability",
              impact: "+0.5-1.0x multiple",
              howTo: "Convert to 40%+ recurring revenue"
            },
            {
              action: "Reduce client concentration",
              impact: "+0.5x multiple",
              howTo: "No client over 20% of revenue"
            },
            {
              action: "Transform vulnerable services",
              impact: "+0.5-1.0x multiple",
              howTo: "AI-enable high-vulnerability services"
            }
          ]
        },

        // Roadmap section
        roadmap: {
          overview: {
            title: 'Your AI Transformation Journey',
            approach: 'A phased approach to transform your agency operations'
          },
          phases: [
            {
              title: 'Phase 1: Foundation',
              duration: '0-3 months',
              actions: priorities.slice(0, 2).flatMap(p => p.actions || ['Assess current capabilities', 'Define transformation goals'])
            },
            {
              title: 'Phase 2: Implementation',
              duration: '3-6 months',
              actions: priorities.slice(1, 3).flatMap(p => p.actions || ['Implement pilot projects', 'Develop staff skills'])
            },
            {
              title: 'Phase 3: Optimization',
              duration: '6-12 months',
              actions: ['Scale successful implementations', 'Optimize for maximum efficiency']
            }
          ]
        },

        // Impact/valuation section
        impact: {
          title: 'Valuation Impact',
          subtitle: 'How AI transformation affects your agency value',
          current: {
            multiple: `${(2 + (overallScore/100 * 2)).toFixed(1)}x`,
            category: 'Current valuation multiple'
          },
          potential: {
            multiple: `${(3 + (overallScore/100 * 3)).toFixed(1)}x`,
            increase: '+1.5x',
            achievableIn: '12-18 months'
          },
          drivers: [
            {
              driver: 'Operational Efficiency',
              currentImpact: 'Medium',
              potentialImpact: 'High',
              action: `Implement ${priorities[0]?.area || 'AI tools'}`
            },
            {
              driver: 'Revenue Predictability',
              currentImpact: 'Low',
              potentialImpact: 'High',
              action: 'Automate client retention processes'
            },
            {
              driver: 'Margin Improvement',
              currentImpact: 'Medium',
              potentialImpact: 'High',
              action: `${efficiencyPotential.costSavings}`
            }
          ]
        },

        // Next steps section
        nextSteps: {
          options: [
            {
              title: 'QuickMap Strategy Session',
              price: 'Free',
              description: '45-minute consultation to review your results and identify immediate opportunities',
              idealFor: 'Understanding your next steps and quick wins'
            },
            {
              title: 'AI Transformation Roadmap',
              price: '$3,500',
              description: 'Comprehensive roadmap development with implementation plan and ROI analysis',
              idealFor: 'Agencies ready to accelerate their AI adoption'
            }
          ],
          testimonial: {
            quote: 'The assessment helped us identify our biggest AI opportunities and prioritize our transformation efforts. We\'ve already seen a 30% increase in efficiency.',
            author: 'Sarah Johnson',
            role: 'CEO, Creative Digital Partners'
          }
        },

        // Include the raw data for reference/debugging
        _rawData: rawResults
      };

      console.log('Adapted results structure:', {
        hasExecutive: !!adaptedResults.executive,
        hasReadiness: !!adaptedResults.readiness,
        hasOpportunities: !!adaptedResults.opportunities,
        hasRoadmap: !!adaptedResults.roadmap,
        hasImpact: !!adaptedResults.impact,
        hasNextSteps: !!adaptedResults.nextSteps
      });

      return adaptedResults;
    }

    // For inhouse-marketing assessment
    else if (assessmentType === 'inhouse-marketing') {
      const overallScore = rawResults.overall || 0;
      const dimensionScores = rawResults.dimensions || {};
      const priorities = (rawResults.priorities || []).slice(0, 3);
      const companySize = rawResults.companySize || 'small';
      const selectedActivities = rawResults.selectedActivities || [];

      return {
        executiveSummary: {
          headline: `Your Marketing Team is ${overallScore < 40 ? 'At Risk' : overallScore < 70 ? 'Adapting' : 'Leading the Curve'}`,
          marketContext: `${overallScore}% of similar marketing teams are further ahead in AI adoption`,
          criticalInsight: "Teams that systematically adopt AI tools save 15+ hours per week per marketer",
          bottomLine: `Your team has ${overallScore < 40 ? 'significant' : overallScore < 70 ? 'moderate' : 'excellent'} potential to leverage AI tools for increased productivity and impact`
        },
        marketPosition: {
          quartile: overallScore < 25 ? "Bottom Quartile" : overallScore < 50 ? "Third Quartile" : overallScore < 75 ? "Second Quartile" : "Top Quartile",
          message: overallScore < 40 ? "Falling Behind" : overallScore < 70 ? "Keeping Pace" : "Ahead of the Curve",
          implication: `You're ${overallScore < 50 ? 'likely to face increasing pressure to deliver more with the same resources' : 'positioned to increase output while reducing workload'}`,
          competitorActions: `Teams with similar profiles are ${overallScore < 50 ? 'increasingly using AI to automate routine tasks' : 'expanding their service offerings while reducing costs'}`,
          timeToAct: overallScore < 40 ? "Urgent - start this week" : overallScore < 70 ? "Important - begin this month" : "Strategic - implement over quarter"
        },
        quickWins: [
          {
            title: "Start with Content Creation",
            why: "Content creation is the easiest area to achieve immediate time savings",
            how: "Use Claude or ChatGPT to draft content, then edit for brand voice",
            specificAction: "Select one upcoming content piece and create it with AI assistance",
            expectedResult: "40-60% time reduction",
            marketContext: "78% of marketing teams start their AI journey with content",
            investment: "Free tools available"
          },
          {
            title: "Automate Social Media Content",
            why: "Repurposing content is time-intensive but necessary",
            how: "Use AI to transform blog posts into multiple social posts",
            specificAction: "Take your latest article and create 10 social posts with AI",
            expectedResult: "10x output in same time period",
            marketContext: "Teams using AI for social see 3x higher posting frequency",
            investment: "Free to $20/month"
          },
          {
            title: "Streamline Reporting",
            why: "Reporting is time-consuming but critical for proving value",
            how: "Automate data collection and insights generation",
            specificAction: "Set up an automated dashboard pulling from your marketing platforms",
            expectedResult: "2-4 hours saved weekly",
            marketContext: "Automated reporting correlates with 2x higher perceived value",
            investment: "$50-100/month"
          }
        ],
        strategicRoadmap: {
          overview: "Your 90-Day AI Implementation Plan",
          phase1: {
            name: "Weeks 1-2: Experimentation",
            focus: "Try free tools and measure time savings",
            actions: [
              "Identify your team's AI champion",
              "Test content creation with Claude or ChatGPT",
              "Time the process and compare to manual work",
              "Document best practices"
            ],
            expectedOutcome: "Proof of concept showing 5-10 hours saved weekly"
          },
          phase2: {
            name: "Weeks 3-6: Systematization",
            focus: "Create processes and workflows",
            actions: [
              "Develop AI writing templates for different content types",
              "Create social media repurposing workflows",
              "Implement automated reporting dashboards",
              "Train team on basic prompting techniques"
            ],
            expectedOutcome: "Team-wide adoption with standard processes"
          },
          phase3: {
            name: "Weeks 7-12: Integration & Expansion",
            focus: "Scale successful pilots across all activities",
            actions: [
              "Integrate AI tools with your existing tech stack",
              "Expand to more advanced use cases",
              "Implement measurement systems for tracking time savings",
              "Develop optimization feedback loops"
            ],
            expectedOutcome: "15+ hours saved per team member weekly"
          },
          investmentGuide: "Initial investment: Free trials → $50-100/month per marketer"
        },
        expectedOutcomes: [
          {
            area: "Content Production",
            keyMetric: "Content pieces per marketer",
            baseline: "2-3 per week",
            target: "8-10 per week",
            timeframe: "90 days"
          },
          {
            area: "Campaign Optimization",
            keyMetric: "Campaign setup & analysis time",
            baseline: "8-10 hours per campaign",
            target: "2-3 hours per campaign",
            timeframe: "60 days"
          },
          {
            area: "Reporting Efficiency",
            keyMetric: "Hours spent on reporting",
            baseline: "5-8 hours weekly",
            target: "1-2 hours weekly",
            timeframe: "30 days"
          },
          {
            area: "Team Satisfaction",
            keyMetric: "Percentage of time on strategic work",
            baseline: "20-30%",
            target: "50-60%",
            timeframe: "90 days"
          }
        ],
        nextSteps: {
          primaryCTA: {
            title: "Book Your Free AI Strategy Session",
            description: "45-minute consultation to review your results and create your custom AI adoption roadmap",
            link: "#book-consultation",
            buttonText: "Book My Strategy Session"
          },
          alternatives: [
            {
              text: "Download Full Report",
              link: "#download",
              description: "Get a PDF of your complete assessment results"
            },
            {
              text: "Join Upcoming Webinar",
              link: "#webinar",
              description: "Attend our 'AI for Marketing Teams' workshop"
            },
            {
              text: "Start with Templates",
              link: "#templates",
              description: "Access our library of proven AI prompts for marketing"
            }
          ]
        },
        _rawData: rawResults
      };
    }

    // For other assessment types, return the raw results
    return rawResults;
  };

  const generateResults = async () => {
    try {
      setIsLoading(true);
      console.log('Starting results calculation for assessment type:', assessmentType);

      // Calculate scores and generate recommendations
      console.log('Calling calculateResults...');
      const calculatedResults = await calculateResults();
      console.log('Raw calculation results:', JSON.stringify(calculatedResults, null, 2));

      if (!calculatedResults) {
        throw new Error('Calculation returned no results');
      }

      // Transform the results format to match what ResultsView expects
      const adaptedResults = adaptResultsFormat(calculatedResults);
      console.log('Adapted results for view:', adaptedResults);

      if (!adaptedResults) {
        throw new Error('Failed to adapt results format');
      }

      setResults(adaptedResults);
      setIsLoading(false);
      console.log('Results dashboard data set successfully');
    } catch (err) {
      console.error('Error generating results:', err);
      console.error('Error stack:', err.stack);
      setError(`Failed to generate results: ${err.message}. Check console for details.`);
      setIsLoading(false);
    }
  };

  const handleBookConsultation = () => {
    // Track conversion
    if (window.gtag) {
      window.gtag('event', 'consultation_click', {
        assessment_type: assessmentType,
        score: results?.scores?.overall || 0,
        category: results?.scores?.categories?.technology?.score > 50 ? 'high' : 'low'
      });
    }

    // Navigate to booking page with context
    const bookingUrl = `https://obsolete.com/quickmap?assessment=${assessmentType}&score=${results?.scores?.overall || 0}`;
    window.open(bookingUrl, '_blank');
  };

  const handleDownloadReport = async () => {
    // Track download
    if (window.gtag) {
      window.gtag('event', 'report_download', {
        assessment_type: assessmentType,
        score: results?.scores?.overall || 0
      });
    }

    // Generate and download PDF
    // This would typically call a cloud function or API
    alert('Report download functionality would be implemented here');
  };

  // Function to download complete assessment data as JSON
  const handleDownloadRawData = async () => {
    try {
      console.log('Preparing comprehensive assessment data download...');

      // Retrieve comprehensive data from localStorage first
      const savedProgress = localStorage.getItem('assessment_progress');
      let savedData = savedProgress ? JSON.parse(savedProgress) : {};

      // Create a complete dataset including all available data
      const comprehensiveData = {
        // Assessment metadata
        assessmentType,
        timestamp: new Date().toISOString(),

        // Individual question responses
        responses: savedData.responses || {},

        // Context data (sector, services, etc.)
        context: savedData.context || {},

        // Results data if available
        results: results || {},

        // Get full state using AssessmentEngine
        engineData: await getFullAssessmentData()
      };

      console.log('Comprehensive data prepared:', comprehensiveData);

      // Create a blob with the data
      const dataToDownload = JSON.stringify(comprehensiveData, null, 2);
      const blob = new Blob([dataToDownload], { type: 'application/json' });

      // Create a URL for the blob
      const url = URL.createObjectURL(blob);

      // Create a temporary anchor element to trigger download
      const a = document.createElement('a');
      a.href = url;
      a.download = `complete-assessment-data-${assessmentType}-${new Date().toISOString().slice(0, 10)}.json`;
      document.body.appendChild(a);
      a.click();

      // Clean up
      setTimeout(() => {
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }, 100);

      console.log('Downloaded complete assessment data');
    } catch (error) {
      console.error('Error downloading comprehensive data:', error);
      alert('Failed to download assessment data: ' + error.message);
    }
  };

  // Helper function to get all data from the assessment engine
  const getFullAssessmentData = async () => {
    try {
      // Create a temporary engine instance to access its methods
      const engine = new AssessmentEngine(assessmentType);
      await engine.initialize();

      // Load any saved progress
      await engine.loadProgress();

      return {
        allResponses: engine.getAllResponses(),
        context: engine.getContext(),
        progressData: engine.getProgress(),
        completionStatus: engine.isComplete()
      };
    } catch (error) {
      console.error('Error retrieving full assessment data:', error);
      return { error: error.message };
    }
  };

  if (isLoading) {
    return (
      <div className={styles.loadingContainer}>
        <div className={styles.loadingAnimation}>
          <div className={styles.spinner} />
          <h2>Analyzing your responses...</h2>
          <p>Generating personalized recommendations</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={styles.errorContainer}>
        <h2>Something went wrong</h2>
        <p>{error}</p>
        <button onClick={generateResults} className={styles.primaryButton}>
          Try Again
        </button>
      </div>
    );
  }

  if (!results) return null;

  // Use custom ResultsView if provided, otherwise use default
  if (ResultsView) {
    console.log('ResultsDashboard: Using custom ResultsView component:', ResultsView.name || 'Anonymous Component');
    console.log('ResultsDashboard: Passing results to ResultsView:', JSON.stringify(results, null, 2));

    // Verify all required sections exist in adapted results before rendering
    if (assessmentType === 'agency-vulnerability') {
      const { executive, readiness, opportunities, roadmap, impact, nextSteps } = results || {};
      console.log('Verifying agency-vulnerability required sections:', {
        hasExecutive: !!executive,
        hasReadiness: !!readiness,
        hasOpportunities: !!opportunities,
        hasRoadmap: !!roadmap,
        hasImpact: !!impact,
        hasNextSteps: !!nextSteps
      });

      // Additional validation of nested structures
      if (executive) {
        console.log('Executive section validation:', {
          hasHeadline: !!executive.headline,
          hasSubheadline: !!executive.subheadline,
          hasKeyMetrics: !!executive.keyMetrics,
          readinessScoreValue: executive.keyMetrics?.readinessScore?.value
        });
      }
    }

    return <ResultsView results={results} onBookConsultation={handleBookConsultation} />;
  }

  // Default results view
  // Extract scores, report, and recommendations, ensuring we handle any missing properties
  const { scores = {}, report = {}, insights = [] } = results || {};
  const recommendations = report?.recommendations || insights || [];

  return (
    <div className={styles.resultsContainer}>
      {/* Header with score */}
      <div className={styles.resultsHeader}>
        <div className={styles.scoreSection}>
          <div className={styles.scoreCircle}>
            <svg width="200" height="200" viewBox="0 0 200 200">
              <circle
                cx="100"
                cy="100"
                r="90"
                stroke="rgba(255,255,255,0.1)"
                strokeWidth="12"
                fill="none"
              />
              <circle
                cx="100"
                cy="100"
                r="90"
                stroke="#ffff66"
                strokeWidth="12"
                fill="none"
                strokeDasharray={`${2 * Math.PI * 90}`}
                strokeDashoffset={`${2 * Math.PI * 90 * (1 - (scores.overall || 0) / 100)}`}
                transform="rotate(-90 100 100)"
                style={{ transition: 'stroke-dashoffset 1.5s ease' }}
              />
            </svg>
            <div className={styles.scoreValue}>
              <span className={styles.scoreNumber}>{scores.overall || 0}</span>
              <span className={styles.scoreLabel}>Overall Score</span>
            </div>
          </div>
        </div>

        <div className={styles.headlineSection}>
          <h1>{report?.executive?.headline || 'Your Assessment Results'}</h1>
          <p className={styles.subheadline}>
            {report?.executive?.subheadline || 'Detailed analysis and recommendations below'}
          </p>

          <div className={styles.keyMetrics}>
            {report?.executive?.keyMetrics && Object.entries(report.executive.keyMetrics).map(([key, metric]) => (
              <div key={key} className={styles.metricCard}>
                <div className={styles.metricValue}>{metric.value}</div>
                <div className={styles.metricLabel}>{metric.label}</div>
                <div className={styles.metricInterpretation}>{metric.interpretation}</div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Section Navigation */}
      <div className={styles.sectionNav}>
        <button
          className={`${styles.navButton} ${activeSection === 'summary' ? styles.active : ''}`}
          onClick={() => setActiveSection('summary')}
        >
          Summary
        </button>
        <button
          className={`${styles.navButton} ${activeSection === 'readiness' ? styles.active : ''}`}
          onClick={() => setActiveSection('readiness')}
        >
          Readiness
        </button>
        <button
          className={`${styles.navButton} ${activeSection === 'opportunities' ? styles.active : ''}`}
          onClick={() => setActiveSection('opportunities')}
        >
          Opportunities
        </button>
        <button
          className={`${styles.navButton} ${activeSection === 'roadmap' ? styles.active : ''}`}
          onClick={() => setActiveSection('roadmap')}
        >
          Roadmap
        </button>
        {scores.categories && (
          <button
            className={`${styles.navButton} ${activeSection === 'valuation' ? styles.active : ''}`}
            onClick={() => setActiveSection('valuation')}
          >
            Valuation Impact
          </button>
        )}
      </div>

      {/* Section Content */}
      <div className={styles.sectionContent}>
        {activeSection === 'summary' && (
          <div className={styles.summarySection}>
            <div className={styles.narrative}>
              <p>{report?.executive?.narrative}</p>
            </div>

            <div className={styles.snapshotGrid}>
              <div className={styles.snapshotCard}>
                <h3>Strengths</h3>
                <ul>
                  {report?.executive?.snapshot?.strengths?.map((strength, i) => (
                    <li key={i}>{strength}</li>
                  ))}
                </ul>
              </div>

              <div className={styles.snapshotCard}>
                <h3>Gaps</h3>
                <ul>
                  {report?.executive?.snapshot?.gaps?.map((gap, i) => (
                    <li key={i}>{gap}</li>
                  ))}
                </ul>
              </div>

              {report?.executive?.snapshot?.quickestWin && (
                <div className={styles.snapshotCard}>
                  <h3>Quickest Win</h3>
                  <h4>{report.executive.snapshot.quickestWin.action}</h4>
                  <p>Impact: {report.executive.snapshot.quickestWin.impact}</p>
                  <p>Effort: {report.executive.snapshot.quickestWin.effort}</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeSection === 'readiness' && (
          <div className={styles.readinessSection}>
            <div className={styles.dimensionsGrid}>
              {report?.readiness?.dimensions?.map((dimension, i) => (
                <div key={i} className={styles.dimensionCard}>
                  <div className={styles.dimensionHeader}>
                    <span className={styles.dimensionIcon}>{dimension.icon}</span>
                    <h3>{dimension.name}</h3>
                  </div>
                  <div className={styles.dimensionScore}>
                    <div className={styles.scoreBar}>
                      <div 
                        className={styles.scoreBarFill} 
                        style={{ width: `${dimension.score}%` }}
                      />
                    </div>
                    <span>{dimension.score}%</span>
                  </div>
                  <p>{dimension.description}</p>
                  <ul className={styles.insights}>
                    {dimension.insights?.map((insight, j) => (
                      <li key={j}>{insight}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeSection === 'opportunities' && (
          <div className={styles.opportunitiesSection}>
            {recommendations?.immediate && (
              <div className={styles.opportunityGroup}>
                <h2>{recommendations.immediate.title || "Quick Wins"}</h2>
                <p>{recommendations.immediate.subtitle}</p>
                <div className={styles.opportunitiesGrid}>
                  {recommendations.immediate.items?.map((item, i) => (
                    <div key={i} className={styles.opportunityCard}>
                      <h3>{item.title}</h3>
                      <p>{item.description || item.why}</p>
                      <div className={styles.opportunityMeta}>
                        <span className={styles.effort}>Effort: {item.effort}</span>
                        <span className={styles.impact}>Impact: {item.impact}</span>
                      </div>
                      {item.specificAction && (
                        <div className={styles.actionBox}>
                          <strong>Action:</strong> {item.specificAction}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {activeSection === 'roadmap' && (
          <div className={styles.roadmapSection}>
            <h2>Your Transformation Journey</h2>
            <div className={styles.phases}>
              {report?.roadmap?.phases?.map((phase, i) => (
                <div key={i} className={styles.phaseCard}>
                  <h3>{phase.name}</h3>
                  <p className={styles.duration}>{phase.duration}</p>
                  <p className={styles.focus}>{phase.focus}</p>
                  <h4>Milestones:</h4>
                  <ul>
                    {phase.milestones?.map((milestone, j) => (
                      <li key={j}>{milestone}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeSection === 'valuation' && scores.categories && (
          <div className={styles.valuationSection}>
            <h2>{report?.valuation?.title}</h2>
            <p>{report?.valuation?.subtitle}</p>

            <div className={styles.valuationGrid}>
              <div className={styles.currentValuation}>
                <h3>Current Valuation</h3>
                <div className={styles.multiple}>{report?.valuation?.current?.multiple}</div>
                <p>{report?.valuation?.current?.category}</p>
              </div>

              <div className={styles.potentialValuation}>
                <h3>Potential Valuation</h3>
                <div className={styles.multiple}>{report?.valuation?.potential?.multiple}</div>
                <div className={styles.increase}>{report?.valuation?.potential?.increase}</div>
                <p>Achievable in {report?.valuation?.potential?.achievableIn}</p>
              </div>
            </div>

            <div className={styles.valuationDrivers}>
              <h3>Value Drivers</h3>
              {report?.valuation?.drivers?.map((driver, i) => (
                <div key={i} className={styles.driverCard}>
                  <h4>{driver.driver}</h4>
                  <p>Current Impact: {driver.currentImpact}</p>
                  <p>Potential: {driver.potentialImpact}</p>
                  <p className={styles.action}>{driver.action}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Call to Action */}
      <div className={styles.ctaSection}>
        <div className={styles.ctaContent}>
          <h2>{report?.nextSteps?.booking?.headline || "Ready to Transform?"}</h2>
          <p>{report?.nextSteps?.booking?.subheadline || "Get your personalized roadmap"}</p>

          <div className={styles.ctaButtons}>
            <button onClick={handleBookConsultation} className={styles.primaryCTA}>
              {report?.nextSteps?.booking?.buttonText || "Book Your QuickMap Session"}
            </button>
            <button onClick={handleDownloadReport} className={styles.secondaryCTA}>
              Download Full Report
            </button>
            <button onClick={handleDownloadRawData} className={styles.secondaryCTA} style={{marginTop: '10px', backgroundColor: '#444'}}>
              Download Raw JSON Data
            </button>
          </div>

          {report?.nextSteps?.urgency && (
            <p className={styles.urgencyMessage}>{report.nextSteps.urgency.message}</p>
          )}
        </div>

        {report?.nextSteps?.testimonial && (
          <div className={styles.testimonial}>
            <blockquote>"{report.nextSteps.testimonial.quote}"</blockquote>
            <cite>
              — {report.nextSteps.testimonial.author}, {report.nextSteps.testimonial.role}
            </cite>
            <p className={styles.result}>{report.nextSteps.testimonial.result}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ResultsDashboard;
