    constructor(customScoring, assessmentType = 'agency-vulnerability') {
        this.customScoring = customScoring;
        this.assessmentType = assessmentType;

        // Get configuration for this assessment type
        const config = ScoringEngine.assessmentTypes[this.assessmentType] || 
                      ScoringEngine.assessmentTypes['agency-vulnerability']; // Fallback

        // Set default weights based on assessment type
        this.defaultWeights = config.defaultWeights || {
            technology: 0.25,
            process: 0.25,
            people: 0.25,
            strategy: 0.25
        };

        this.dimensionConfig = config.dimensionConfig || {};

        // Allow custom scoring to override default weights
        this.weights = this.customScoring?.weights || this.defaultWeights;
    }

    calculate(responses, context) {
        console.log('Starting score calculation with responses:', Object.keys(responses).length);
        console.log('Context for calculation:', context);

        try {
            // Base scoring logic used by all assessments
            let scores = this.calculateDimensionScores(responses);

            // Apply custom scoring if provided
            if (this.customScoring?.calculate) {
                try {
                    const customScores = this.customScoring.calculate(scores, responses, context);
                    if (customScores) scores = customScores;
                } catch (error) {
                    console.error('Error in custom scoring calculation:', error);
                }
            }

            // Apply context-based adjustments
            scores = this.applyContextAdjustments(scores, context);

            // Calculate overall score
            const overall = this.calculateOverallScore(scores);

            // Generate insights
            const result = {
                scores: {
                    overall: overall,
                    categories: scores
                },
                insights: this.generateInsights({ overall, dimensions: scores }, context),
                report: this.generateReport({ overall, dimensions: scores }, context)
            };

            console.log('Final calculation result:', result);
            return result;
        } catch (error) {
            console.error('Error in score calculation:', error);

            // Return a fallback result to prevent UI errors
            return {
                scores: {
                    overall: 50,
                    categories: {
                        technology: { score: 50 },
                        process: { score: 50 },
                        people: { score: 50 },
                        strategy: { score: 50 }
                    }
                },
                insights: [{
                    type: 'warning',
                    title: 'Score Calculation Issue',
                    description: 'There was an issue calculating your detailed scores.'
                }],
                report: this.generateDefaultReport()
            };
        }
    }

    calculateDimensionScores(responses) {
        // Default implementation for base dimension calculation
        console.log('Calculating dimension scores with responses:', responses);

        // Extract question responses and organize them by category
        const categories = {};

        // Initialize default categories with zero scores
        const defaultCategories = ['technology', 'process', 'people', 'strategy'];
        defaultCategories.forEach(cat => {
            categories[cat] = { score: 0, count: 0, responses: [] };
        });

        // Process each response
        Object.entries(responses).forEach(([questionId, answer]) => {
            // Skip non-numeric responses or special properties
            if (typeof answer !== 'number' || questionId === 'selectedServices') return;

            // Determine category from question ID - default to 'technology' if unknown
            // Format might be: tech_01, people_02, etc.
            const category = questionId.split('_')[0] || 'technology';
            const normalizedCategory = this.normalizeCategory(category);

            // If category not initialized, create it
            if (!categories[normalizedCategory]) {
                categories[normalizedCategory] = { score: 0, count: 0, responses: [] };
            }

            // Add the score (assuming questions are scored 0-5)
            categories[normalizedCategory].score += answer;
            categories[normalizedCategory].count++;
            categories[normalizedCategory].responses.push({ id: questionId, value: answer });
        });

        // Calculate average scores for each category
        const dimensionScores = {};
        Object.entries(categories).forEach(([category, data]) => {
            dimensionScores[category] = {
                score: data.count > 0 ? Math.round((data.score / (data.count * 5)) * 100) : 0,
                responses: data.responses
            };
        });

        console.log('Calculated dimension scores:', dimensionScores);
        return dimensionScores;
    }

    calculateOverallScore(dimensionScores) {
        // Default implementation for overall score calculation
        console.log('Calculating overall score with dimension scores:', dimensionScores);

        let weightedSum = 0;
        let totalWeight = 0;

        // Calculate weighted sum of dimension scores
        Object.entries(dimensionScores).forEach(([dimension, data]) => {
            const weight = this.weights[dimension] || 0.25; // Default equal weight if not specified
            weightedSum += (data.score || 0) * weight;
            totalWeight += weight;
        });

        // Calculate overall score (rounded to nearest integer)
        const overallScore = totalWeight > 0 ? Math.round(weightedSum / totalWeight) : 50;

        console.log('Calculated overall score:', overallScore);
        return overallScore;
    }

    applyContextAdjustments(scores, context) {
        // Apply size, industry, or other contextual adjustments
        console.log('Applying context adjustments with context:', context);

        // If no context, return scores unchanged
        if (!context) return scores;

        // Example: Adjust for company size
        if (context.companySize) {
            // Small companies might get a boost in technology score
            if (context.companySize === 'small' && scores.technology) {
                scores.technology.score = Math.min(100, scores.technology.score + 5);
            }
        }

        // Example: Adjust for selected services
        if (context.selectedServices) {
            const serviceCount = Object.keys(context.selectedServices).length;
            // More focused companies (fewer services) might score higher in strategy
            if (serviceCount <= 2 && scores.strategy) {
                scores.strategy.score = Math.min(100, scores.strategy.score + 5);
            }
        }

        return scores;
    }

    generateInsights(scores, context) {
        // Generate insights based on scores
        const insights = [];

        // Only proceed if we have valid scores
        if (!scores) return insights;

        // Generate overall insights
        if (typeof scores.overall === 'number') {
            if (scores.overall < 30) {
                insights.push({
                    type: 'critical',
                    title: 'Significant Improvement Needed',
                    description: 'Your marketing maturity score indicates substantial room for improvement across multiple dimensions.'
                });
            } else if (scores.overall < 60) {
                insights.push({
                    type: 'warning',
                    title: 'Building Foundations',
                    description: 'You have some marketing fundamentals in place, but there are key areas that need strengthening.'
                });
            } else if (scores.overall < 80) {
                insights.push({
                    type: 'positive',
                    title: 'Solid Performance',
                    description: 'Your marketing operations show good maturity with specific opportunities for optimization.'
                });
            } else {
                insights.push({
                    type: 'excellent',
                    title: 'Marketing Excellence',
                    description: 'Your marketing organization demonstrates high levels of maturity and effectiveness.'
                });
            }
        }

        // Generate dimension-specific insights
        Object.entries(scores.dimensions || {}).forEach(([dimension, data]) => {
            if (typeof data.score === 'number' && data.score < 50) {
                insights.push({
                    type: 'opportunity',
                    dimension: dimension,
                    title: `${this.formatDimension(dimension)} Opportunity`,
                    description: `Your ${this.formatDimension(dimension)} score suggests this is a high-priority area for improvement.`
                });
            }
        });

        return insights;
    }
